import React from "react";
import { useLocation } from "react-router-dom"; // Import the useLocation hook
import Card from "../components/Card";
import data from "../data/data";

const Homestay = ({ chatbotVisible }) => {
  // Use the useLocation hook to get the current location object
  const location = useLocation();

  // Filter the data array to include only items with type 'homestay'
  const homestayData = data.filter((card) => card.type === "homestay");

  // Check if the URL contains the query parameter 'phobjikha'
  const isPhobjikhaSearch = location.search.includes("phobjikha");

  // Sort the homestayData array based on the 'isPhobjikhaSearch' flag
  homestayData.sort((a, b) => {
    if (isPhobjikhaSearch) {
      if (a.location === "Phobjikha, Bhutan") {
        return -1; // 'a' comes before 'b'
      } else if (b.location === "Phobjikha, Bhutan") {
        return 1; // 'b' comes before 'a'
      }
    }
    return 0; // no change in order for other locations or when 'isPhobjikhaSearch' is false
  });

  return (
    <>
      <div className="flex flex-wrap justify-between overflow-hidden w-full gap-y-12">
        {homestayData.map((card, index) => (
          <Card key={index} data={card} chatbotVisible={chatbotVisible} />
        ))}
      </div>
    </>
  );
};

export default Homestay;
