import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { supabase } from "../../utils/supabaseClient";
import Sidebar from "../../components/Sidebar";

function GuestDetails() {
  const { bookingId } = useParams();
  const navigate = useNavigate();
  const [booking, setBooking] = useState(null);

  useEffect(() => {
    fetchBookingDetails();
  }, []);

  async function fetchBookingDetails() {
    const { data, error } = await supabase
      .from("booking")
      .select("*")
      .eq("bookingid", bookingId)
      .single();

    if (error) {
      console.error("Error fetching booking details:", error);
    } else {
      setBooking(data);
    }
  }

  if (!booking) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <main className="flex-1 p-8">
        <button 
          className="bg-gray-200 px-4 py-2 rounded-md mb-6"
          onClick={() => navigate(-1)}
        >
          ‚Üê Back
        </button>
        <h2 className="text-2xl font-bold">Guest Details</h2>

        <div className="mt-6 bg-white p-6 rounded-lg shadow-md">
          <div className="grid grid-cols-2 gap-4">
            <p><strong>Name:</strong> {booking.guestname}</p>
            <p><strong>Email:</strong> {booking.guestemail}</p>
            <p><strong>Check-in:</strong> {booking.checkin}</p>
            <p><strong>Check-out:</strong> {booking.checkout}</p>
            <p><strong>Adults:</strong> {booking.adult}</p>
            <p><strong>Children:</strong> {booking.children}</p>
            <p><strong>Guest Type:</strong> {booking.guesttype}</p>
          </div>
          <div className="mt-6">
            <p><strong>Message:</strong></p>
            <p className="text-gray-600 mt-2">{booking.message}</p>
          </div>
        </div>

        {/* Accept & Decline Buttons */}
        <div className="flex space-x-4 mt-6">
          <button className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-700">Accept</button>
          <button className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-700">Decline</button>
        </div>
      </main>
    </div>
  );
}

export default GuestDetails;
