import React from "react";
import Card from "../components/Card";
import data from "../data/data";

const Trending = ({ chatbotVisible }) => {
  // Sort the data array in descending order of reviews
  const sortedData = [...data].sort((a, b) => b.reviews - a.reviews);

  // Filter out items of type "restaurant" and "packages"
  const filteredData = sortedData.filter(
    (card) => card.type !== "restaurant" && card.type !== "packages"
  );

  return (
    <>
      <div className="flex flex-wrap justify-between overflow-hidden w-full gap-y-12">
        {filteredData.map((card, index) => (
          <Card key={index} data={card} chatbotVisible={chatbotVisible} />
        ))}
      </div>
    </>
  );
};

export default Trending;
