import React from "react";
import { useParams } from "react-router-dom";
import homestaysData from "../data/homestaysData";

const HomestayDetail = () => {
  const { id } = useParams();

  let selectedHomestay;
  Object.values(homestaysData).forEach((homestayList) => {
    homestayList.forEach((homestay) => {
      if (homestay.id === parseInt(id)) {
        selectedHomestay = homestay;
      }
    });
  });

  if (!selectedHomestay) {
    return <p className="text-center text-gray-500">Homestay not found.</p>;
  }

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h1 className="text-3xl font-semibold">{selectedHomestay.name}</h1>
      <p className="text-gray-500">{selectedHomestay.location}</p>

      <img
        src={selectedHomestay.image}
        alt={selectedHomestay.name}
        className="w-full h-64 object-cover rounded-lg my-4"
      />

      <p className="text-gray-700">{selectedHomestay.description}</p>
      <p className="mt-2 text-lg font-bold">Nu.{selectedHomestay.price} / night</p>

      <div className="mt-4">
        <h2 className="text-xl font-semibold">Facilities</h2>
        <ul className="list-disc list-inside text-gray-600">
          {selectedHomestay.facilities.map((facility, index) => (
            <li key={index}>{facility}</li>
          ))}
        </ul>
      </div>

      <p className="mt-2 text-yellow-500 font-semibold">‚≠ê {selectedHomestay.rating}</p>
    </div>
  );
};

export default HomestayDetail;
