import React, { useState } from 'react';
import { SignOutButton } from "@clerk/clerk-react";
import { FaTachometerAlt, FaHome, FaSignOutAlt } from 'react-icons/fa';

function Sidebar() {
  const [active, setActive] = useState("Dashboard");

  return (
    <div className='h-screen bg-[#FF6600] w-64 flex flex-col justify-between'>
        <div>
            <div className='flex justify-center mt-6'>
                <img src='/white-logo.svg' alt='Logo' width={40} height={40}/>
                <p className='tracking-tight text-[1.8rem] font-bold text-white'>
                    <span className='font-semibold'>Your</span>Bhutan
                </p>
            </div>
            <nav className='flex mt-12 justify-start'>
                <ul className='w-full'>
                  <li 
                    className={`py-4 text-xl text-white flex items-center gap-3 cursor-pointer  px-4 transition-all w-full ${active === "Dashboard" ? "bg-white bg-opacity-50" : "hover:bg-white hover:bg-opacity-50"}`}
                    onClick={() => setActive("Dashboard")}
                  >
                    <FaTachometerAlt /> Dashboard
                  </li>
                  <li 
                    className={`text-xl text-white mt-4 cursor-pointer flex items-center gap-3  px-4 transition-all w-full ${active === "Homestay" ? "bg-white bg-opacity-50" : "hover:bg-white hover:bg-opacity-50"}`}
                    onClick={() => setActive("Homestay")}
                  >
                    <FaHome /> Homestay
                  </li>
                </ul>
            </nav>
        </div>

        {/* Logout Section */}
        <div className='mb-6 flex justify-start w-full'>
        <SignOutButton>
            <button className='flex items-center gap-3 text-xl text-white cursor-pointer hover:bg-white hover:bg-opacity-50  px-4 py-3 w-full'>
                <FaSignOutAlt /> Logout
            </button>
        </SignOutButton>
        </div>
    </div>
  );
}

export default Sidebar;
