import React, { useState } from "react";
// import BannerImg from "../img/Hosting Info Section.png";
import BannerImg2 from "../img/about section.png";
import BannerImg3 from "../img/fall-banner.png";
import data from "../data/data";
import Card from "../components/Card";
import EventSlider from "../components/EventSlider";

const Home = ({ chatbotVisible }) => {
  const [imagesLoaded, setImagesLoaded] = useState(false);

  // Homestay
  const homestayData = data.filter((card) => card.type === "homestay");
  homestayData.sort((a, b) => b.reviews - a.reviews);
  const topFourHomestays = homestayData.slice(0, 4);

  // Hotel
  const hotelData = data.filter((card) => card.type === "hotel");
  hotelData.sort((a, b) => b.reviews - a.reviews);
  const topFourHotel = hotelData.slice(0, 4);

  // Packages
  const packagesData = data.filter((card) => card.type === "packages");
  packagesData.sort((a, b) => b.reviews - a.reviews);
  const topFourPackages = packagesData.slice(0, 4);

  return (
    <>
      <div
        className={`flex flex-col transition-all duration-500 ${
          chatbotVisible ? "gap-[5rem]" : "gap-[10rem]"
        }`}
      >
        <section className="w-full h-full bg-cover bg-center relative">
          {/* <img
            src={BannerImg}
            alt=""
            className={`absolute z-[-10] w-full ${
              chatbotVisible ? "h-full" : ""
            }`}
          />
          <div
            className={`h-full ${
              chatbotVisible
                ? "px-[3rem] py-[2rem] transition-all duration-500"
                : "px-[5rem] py-[4rem] transition-all duration-500"
            }`}
          >
            <h2
              className={`font-semibold leading-tight tracking-tight ${
                chatbotVisible
                  ? "text-[1.5rem] transition-all duration-500"
                  : "text-[3rem] transition-all duration-500"
              }`}
            >
              Upcoming Events in <br /> Minutes
            </h2>
            <p
              className={`text-[#9A9A9A] ${
                chatbotVisible
                  ? "text-sm pt-4 pb-2 transition-all duration-500 w-[45%]"
                  : "text-md pt-6 pb-4 transition-all duration-500 w-[30%]"
              }`}
            >
              Get the personalized travel plan just for you, guided by traveler
              trips and reviews of all the best places in Bhutan!
            </p>
            <button
              className={`bg-black/[80%] text-white rounded-full mt-4 hover:bg-black/[100%] ${
                chatbotVisible
                  ? "p-3 px-5 text-sm transition-all duration-500"
                  : "p-4 px-8 transition-all duration-500"
              }`}
            >
              Plan your trip with AI
            </button>
          </div> */}
          <EventSlider
            chatbotVisible={chatbotVisible}
            onLoad={() => setImagesLoaded(true)}
          />
        </section>

        <section className="homestay">
          <h2 className="section_title font-bold tracking-tight text-[2rem] pb-3">
            Homestays
          </h2>
          <h4 className="subtitle text-[#484848] pb-8">
            Homestays in Bhutan provide an authentic cultural immersion.
          </h4>

          <div className="flex flex-wrap overflow-hidden w-full justify-between gap-y-8">
            <div className="flex flex-wrap justify-between overflow-hidden w-full gap-y-12">
              {topFourHomestays.map((card, index) => (
                <Card key={index} data={card} chatbotVisible={chatbotVisible} />
              ))}
            </div>
          </div>
        </section>

        <section className="w-full h-full bg-cover bg-center relative">
          <img
            src={BannerImg2}
            alt=""
            className="absolute z-[-10] w-full"
            onLoad={() => setImagesLoaded(true)}
          />
          <div
            className={`h-full ${
              chatbotVisible
                ? "px-[3rem] py-[2rem] transition-all duration-500"
                : "px-[5rem] py-[4rem] transition-all duration-500"
            }`}
          >
            <h2
              className={`font-semibold leading-tight tracking-tight ${
                chatbotVisible
                  ? "text-[1.8rem] transition-all duration-500"
                  : "text-[3rem] transition-all duration-500"
              }`}
            >
              Discover Car & Bike Rental: <br /> Your Key to Exploration and
              Freedom
            </h2>
            <p
              className={`text-[#9A9A9A] ${
                chatbotVisible
                  ? "text-sm pt-4 pb-2 transition-all duration-500 w-[70%]"
                  : "text-md pt-6 pb-4 transition-all duration-500 w-[50%]"
              }`}
            >
              Explore Bhutan at your own pace with our reliable transport rental
              service. Discover the breathtaking landscapes, serene monasteries,
              and vibrant culture of this Himalayan kingdom. Whether you prefer
              a car or a bike, we provide the means for you to create your
              unique Bhutanese adventure.
            </p>
            <button
              className={`bg-black/[80%] text-white rounded-full mt-4 hover:bg-black/[100%] ${
                chatbotVisible
                  ? "p-3 px-5 text-sm transition-all duration-500"
                  : "p-4 px-8 transition-all duration-500"
              }`}
            >
              Book Your Journey Now
            </button>
          </div>
        </section>

        <section className="tours">
          <h2 className="section_title font-bold tracking-tight text-[2rem] pb-3">
            Tour Packages
          </h2>
          <h4 className="subtitle text-[#484848] pb-8">
            Our tour packages are your gateway to an unforgettable Bhutanese
            adventure
          </h4>

          <div className="flex flex-wrap overflow-hidden w-full justify-between gap-y-8">
            <div className="flex flex-wrap justify-between overflow-hidden w-full gap-y-12">
              {topFourPackages.map((card, index) => (
                <Card key={index} data={card} chatbotVisible={chatbotVisible} />
              ))}
            </div>
          </div>
        </section>

        <section className="hotels">
          <h2 className="section_title font-bold tracking-tight text-[2rem] pb-3">
            Hotels
          </h2>
          <h4 className="subtitle text-[#484848] pb-8">
            Bhutanese hotels offer a blend of tradition and Himalayan beauty.
          </h4>

          <div className="flex flex-wrap overflow-hidden w-full justify-between gap-y-8">
            <div className="flex flex-wrap justify-between overflow-hidden w-full gap-y-12">
              {topFourHotel.map((card, index) => (
                <Card key={index} data={card} chatbotVisible={chatbotVisible} />
              ))}
            </div>
          </div>
        </section>

        <section className="w-full h-full bg-cover bg-center relative mb-[10rem]">
          <img
            src={BannerImg3}
            alt=""
            className="w-full"
            onLoad={() => setImagesLoaded(true)}
          />
          <div
            className={`absolute bottom-0 text-[#ffffff]  ${
              chatbotVisible
                ? "px-[3rem] py-[2rem] transition-all duration-500"
                : "px-[5rem] py-[4rem] transition-all duration-500"
            }`}
          >
            <h2
              className={`font-semibold leading-tight tracking-tight ${
                chatbotVisible
                  ? "text-[1.8rem] transition-all duration-500"
                  : "text-[3rem] transition-all duration-500"
              }`}
            >
              Your go-to-guide for fall
            </h2>
            <p
              className={` ${
                chatbotVisible
                  ? "text-md pt-4 pb-2 transition-all duration-500"
                  : "text-lg pt-6 pb-4 transition-all duration-500"
              }`}
            >
              How to take on the seasonâ€”at home and afar
            </p>
            <button
              className={`bg-black/[80%] text-white rounded-full mt-4 hover:bg-black/[100%] ${
                chatbotVisible
                  ? "p-3 px-7 text-sm transition-all duration-500"
                  : "p-4 px-10 transition-all duration-500"
              }`}
            >
              Read More
            </button>
          </div>
        </section>
      </div>
    </>
  );
};

export default Home;
