import React, { useEffect, useState } from "react";
import { supabase } from "../../utils/supabaseClient";
import Sidebar from "../../components/Sidebar";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const dzongkhags = [
  "Bumthang", "Chhukha", "Dagana", "Gasa", "Haa", "Lhuentse", "Mongar", 
  "Paro", "Pema Gatshel", "Punakha", "Samdrup Jongkhar", "Samtse", "Sarpang",
  "Thimphu", "Trashi Yangtse", "Trashigang", "Trongsa", "Tsirang", "Wangdue Phodrang", "Zhemgang"
];

function Homestay() {
  const [user, setUser] = useState(null);
  const [homestay, setHomestay] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [homestayId, setHomestayId] = useState(null);

  const [formData, setFormData] = useState({
    name: "",
    description: "",
    facilities: "",
    availabilitydate: [],
    location: "",
    googlemapurl: "",
    images: [],
    pricepernight: "",
  });

  const [imageFiles, setImageFiles] = useState([]);

  useEffect(() => {
    async function fetchUser() {
      const { data, error } = await supabase.auth.getUser();
      if (error) {
        console.error("Error fetching user:", error);
      } else {
        setUser(data.user);
        fetchHomestay(data.user.id); // Fetch homestay only when user is available
      }
    }
    fetchUser();
  }, []);

  async function fetchHomestay(userId) {
    setLoading(true);
    try {
      const { data: hostData, error: hostError } = await supabase
        .from("host")
        .select("hostid")
        .eq("userid", userId) // Make sure this matches your table structure
        .single();

      if (hostError || !hostData) {
        console.error("Error fetching host ID:", hostError);
        setLoading(false);
        return;
      }

      const hostId = hostData.hostid;

      const { data, error } = await supabase
        .from("homestay")
        .select("*")
        .eq("hostid", hostId)
        .single();

      if (error) {
        console.error("Error fetching homestay:", error);
      } else {
        setHomestay(data);
        setHomestayId(data.homestayid);
        setFormData({
          name: data.name,
          description: data.description,
          facilities: data.facilities,
          availabilitydate: data.availabilitydate,
          location: data.location,
          googlemapurl: data.googlemapurl,
          images: data.images,
          pricepernight: data.pricepernight,
        });
      }
    } catch (err) {
      console.error("Unexpected error:", err);
    }
    setLoading(false);
  }

  function handleChange(e) {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  }

  function handleDateChange(date) {
    if (!date) return;
    setFormData(prevState => ({
      ...prevState,
      availabilitydate: [...prevState.availabilitydate, date]
    }));
  }

  function removeDate(index) {
    setFormData(prevState => ({
      ...prevState,
      availabilitydate: prevState.availabilitydate.filter((_, i) => i !== index)
    }));
  }

  async function handleSubmit(e) {
    e.preventDefault();
    if (!user) return;

    const { data: hostData, error: hostError } = await supabase
      .from("host")
      .select("hostid")
      .eq("userid", user.id)
      .single();

    if (hostError || !hostData) {
      console.error("Error fetching host ID:", hostError);
      return;
    }

    const hostId = hostData.hostid;

    let imageUrls = [];

    for (const file of imageFiles) {
      const { data, error } = await supabase.storage
        .from("homestay-images")
        .upload(`homestays/${user.id}/${file.name}`, file, { cacheControl: "3600" });

      if (error) {
        console.error("Error uploading image:", error);
      } else {
        imageUrls.push(data.path);
      }
    }

    console.log("Final image URLs:", imageUrls);

    if (editing) {
      // Update homestay
      const { error } = await supabase.from("homestay").update({
        name: formData.name,
        description: formData.description,
        facilities: formData.facilities,
        availabilitydate: formData.availabilitydate.map(date => date.toISOString().split("T")[0]),
        location: formData.location,
        googlemapurl: formData.googlemapurl,
        images: [...formData.images, ...imageUrls],
        pricepernight: formData.pricepernight,
      }).eq("homestayid", homestayId);

      if (error) {
        console.error("Error updating homestay:", error);
      } else {
        console.log("Homestay updated successfully!");
        setEditing(false);
        fetchHomestay(user.id);
      }
    } else {
      // Insert new homestay
      const { error } = await supabase.from("homestay").insert([
        {
          hostid: hostId,
          name: formData.name,
          description: formData.description,
          facilities: formData.facilities,
          availabilitydate: formData.availabilitydate.map(date => date.toISOString().split("T")[0]),
          location: formData.location,
          googlemapurl: formData.googlemapurl,
          images: imageUrls,
          pricepernight: formData.pricepernight,
        },
      ]);

      if (error) {
        console.error("Error adding homestay:", error);
      } else {
        console.log("Homestay added successfully!");
        fetchHomestay(user.id);
      }
    }
  }

  if (loading) return <div className="flex justify-center items-center h-screen">Loading...</div>;

  return (
    <div className="flex h-screen">
      <Sidebar />
      <main className="flex-1 p-8">
        <h2 className="text-2xl font-semibold">Homestay Management</h2>
        <form onSubmit={handleSubmit} className="mt-4 space-y-4">
          <input type="text" name="name" placeholder="Homestay Name" value={formData.name} onChange={handleChange} required />
          <textarea name="description" placeholder="Description" value={formData.description} onChange={handleChange} required />
          <input type="text" name="facilities" placeholder="Facilities" value={formData.facilities} onChange={handleChange} required />

          <select name="location" value={formData.location} onChange={handleChange} required>
            <option value="" disabled>Select Dzongkhag</option>
            {dzongkhags.map((dzongkhag, index) => (
              <option key={index} value={dzongkhag}>{dzongkhag}</option>
            ))}
          </select>

          <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded">
            {editing ? "Update Homestay" : "Add Homestay"}
          </button>
        </form>

        {homestay && (
          <div className="mt-6">
            <h3 className="text-xl font-semibold">{homestay.name}</h3>
            <p>{homestay.description}</p>
            <button onClick={() => setEditing(true)} className="bg-blue-500 text-white px-4 py-2 rounded mt-4">
              Edit Homestay
            </button>
          </div>
        )}
      </main>
    </div>
  );
}

export default Homestay;
