  "Bumthang", "Chhukha", "Dagana", "Gasa", "Haa", "Lhuentse", "Mongar", 
  "Paro", "Pema Gatshel", "Punakha", "Samdrup Jongkhar", "Samtse", "Sarpang",
  "Thimphu", "Trashi Yangtse", "Trashigang", "Trongsa", "Tsirang", "Wangdue Phodrang", "Zhemgang"
];

function Homestay() {
  const { user } = useUser();
  const [homestay, setHomestay] = useState(null);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    facilities: "",
    availabilitydate: [],
    location: "",
    googlemapurl: "",
    images: [],
    pricepernight: "",
  });
  const [imageFiles, setImageFiles] = useState([]);
  const BUCKET_NAME = "homestay-images";

  useEffect(() => {
    if (user) {
      fetchHomestay();
    }
  }, [user]);

  async function fetchHomestay() {
    setLoading(true);
    try {
      const { data: hostData, error: hostError } = await supabase
        .from("host")
        .select("hostid")
        .eq("clerkuserid", user.id)
        .single();

      if (hostError || !hostData) {
        console.error("Error fetching host ID:", hostError);
        setLoading(false);
        return;
      }

      const hostId = hostData.hostid;

      const { data, error } = await supabase
        .from("homestay")
        .select("*")
        .eq("hostid", hostId)
        .single();

      if (error) {
        console.error("Error fetching homestay:", error);
      } else {
        setHomestay(data);
      }
    } catch (err) {
      console.error("Unexpected error:", err);
    }
    setLoading(false);
  }

  async function uploadImages() {
    let imageUrls = [];
    for (const file of imageFiles) {
      console.log("Uploading file:", file.name);

      const { data, error } = await supabase.storage
        .from(BUCKET_NAME)
        .upload(`homestays/${user.id}/${Date.now()}_${file.name}`, file, { cacheControl: "3600" });

      if (error) {
        console.error("Error uploading image:", error);
        continue;
      }

      console.log("Uploaded file path:", data.path);

      const publicURL = supabase.storage.from(BUCKET_NAME).getPublicUrl(data.path);
      console.log("Generated Public URL:", publicURL.publicUrl);
      imageUrls.push(publicURL.publicUrl);
    }
    return imageUrls;
  }

  async function handleSubmit(e) {
    e.preventDefault();
    if (!user) return;

    const { data: hostData, error: hostError } = await supabase
      .from("host")
      .select("hostid")
      .eq("clerkuserid", user.id)
      .single();

    if (hostError || !hostData) {
      console.error("Error fetching host ID:", hostError);
      return;
    }

    const hostId = hostData.hostid;
    const imageUrls = await uploadImages();

    if (imageUrls.length === 0) {
      console.error("No images uploaded! Aborting insert.");
      return;
    }

    console.log("Inserting into database with image URLs:", imageUrls);

    const { error } = await supabase.from("homestay").insert([
      {
        hostid: hostId,
        name: formData.name,
        description: formData.description,
        facilities: formData.facilities,
        availabilitydate: formData.availabilitydate.map(date => date.toISOString().split("T")[0]),
        location: formData.location,
        googlemapurl: formData.googlemapurl,
        images: imageUrls,
        pricepernight: formData.pricepernight,
      },
    ]);

    if (error) {
      console.error("Error adding homestay:", error);
    } else {
      console.log("Homestay added successfully!");
      fetchHomestay();
    }
  }

  return (
    <div className="flex h-screen">
      <Sidebar />
      <main className="flex-1 p-8">
        <h2 className="text-2xl font-semibold">Homestay Management</h2>
        <input type="file" multiple accept="image/*" onChange={(e) => setImageFiles([...e.target.files])} className="w-full border p-2 rounded" />
        <button type="submit" onClick={handleSubmit} className="bg-green-500 text-white px-4 py-2 rounded">Add Homestay</button>
      </main>
    </div>
  );
}

export default Homestay;
