import { useState } from "react";
import { supabase } from "../../utils/supabaseClient";

function HostSignup() {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    fullName: "",
    contact: "",
  });

  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  function handleChange(e) {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  }

  async function handleSignup(e) {
    e.preventDefault();
    setLoading(true);
    setError(null);

    // Step 1: Register the host in Supabase Auth
    const { data, error: signUpError } = await supabase.auth.signUp({
      email: formData.email,
      password: formData.password,
    });

    if (signUpError) {
      setError(signUpError.message);
      setLoading(false);
      return;
    }

    console.log("Supabase Auth User:", data);

    // Step 2: Ensure user is available
    if (!data.user) {
      setError("Signup successful, but user data is missing.");
      setLoading(false);
      return;
    }

    const user = data.user;

    // Step 3: Insert user into the "host" table
    const { error: insertError } = await supabase
      .from("host")
      .insert([
        {
          hostid: user.id, // Ensure this matches the column name in your database
          hostname: formData.fullName,
          hostemail: formData.email,
          hostcontact: formData.contact || null,
        },
      ]);

    if (insertError) {
      console.error("Error inserting host:", insertError);
      setError("Error saving host: " + insertError.message);
    } else {
      setSuccess(true);
      console.log("Host added successfully!");
    }

    setLoading(false);
  }

  return (
    <div className="flex justify-center items-center h-screen">
      <form onSubmit={handleSignup} className="w-96 p-6 bg-white shadow-lg rounded-lg">
        <h2 className="text-2xl font-semibold mb-4">Host Sign Up</h2>

        {error && <p className="text-red-500">{error}</p>}
        {success && (
          <p className="text-green-500">Sign-up successful! You can now log in.</p>
        )}

        <input
          type="text"
          name="fullName"
          placeholder="Full Name"
          value={formData.fullName}
          onChange={handleChange}
          required
          className="w-full border p-2 rounded mb-2"
        />

        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
          className="w-full border p-2 rounded mb-2"
        />

        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
          className="w-full border p-2 rounded mb-2"
        />

        <input
          type="text"
          name="contact"
          placeholder="Contact (optional)"
          value={formData.contact}
          onChange={handleChange}
          className="w-full border p-2 rounded mb-2"
        />

        <button
          type="submit"
          className="w-full bg-green-500 text-white px-4 py-2 rounded"
          disabled={loading}
        >
          {loading ? "Signing Up..." : "Sign Up"}
        </button>
      </form>
    </div>
  );
}

export default HostSignup;
