// Header.js
import React, { useState, useEffect } from "react";
import logo from "../assets/svg/logo.svg";
import { Link, useLocation } from "react-router-dom";
import SubHeader from "./SubHeader";

import { useNavigate } from "react-router-dom";

const Header = ({ onSearch, chatbotVisible }) => {
  const location = useLocation();
  const navigate = useNavigate();
  const [scrolling, setScrolling] = useState(false);

  const handleHostNavigation = () => {
    const isAuthenticated = localStorage.getItem("userToken"); // Example authentication check
    if (isAuthenticated) {
      navigate("/host"); // Go to host page if logged in
    } else {
      navigate("/login"); // Redirect to login if not authenticated
    }
  };

  return (
    <header className="parent-header w-full flex flex-col justify-center items-center sticky top-0 z-10 transition-all bg-white ${scrolling ? 'shadow-md' : ''}">
      <div className="main-header w-full flex justify-between px-[5%] py-4">
        <Link to="/">
          <div className="w-full flex justify-start items-center">
            <img src={logo} alt="" className="h-12" />
            <h1 className="tracking-tight text-[1.8rem] font-semibold">
              <span className="text-[#6E6E6E]">Your</span>Bhutan
            </h1>
          </div>
        </Link>

        <div className="w-full flex justify-center items-center">
          <ul className="w-full nav_links flex items-center justify-center gap-[3rem]">
            <li className={location.pathname === "/" ? "!border-b-2 !border-[#FF6600] !pb-1" : "hover:border-b-2 hover:border-[#FF6600] hover:pb-1 hover:transition-all hover:duration-75"}>
              <Link to="/">Home</Link>
            </li>
            <li className={location.pathname === "/aboutus" ? "!border-b-2 !border-[#FF6600] pb-1" : "hover:border-b-2 hover:border-[#FF6600] hover:pb-1 hover:transition-all hover:duration-75"}>
              <Link to="/aboutus">About Us</Link>
            </li>
            <li className={location.pathname === "/contact" ? "border-b-2 border-[#FF6600] pb-1" : "hover:border-b-2 hover:border-[#FF6600] hover:pb-1 hover:transition-all hover:duration-75"}>
              <Link to="/contact">Contact</Link>
            </li>

            {/* Host Navigation with Authentication Check */}
            <li
              className={location.pathname === "/host" ? "border-b-2 border-[#FF6600] pb-1" : "hover:border-b-2 hover:border-b-[#FF6600] hover:pb-1 hover:transition-all hover:duration-75"}
            >
              <button onClick={handleHostNavigation} className="focus:outline-none">Host Your Home</button>
            </li>
          </ul>
        </div>
      </div>
      <SubHeader />
    </header>
  );
};

export default Header;
