import React from "react";
import { useState, useRef } from "react";

const PaymentConfirmation = ( ) => {
  const handleClick = (e) => {
    console.log(1);
  };

  const inputRefs = [useRef(null), useRef(null), useRef(null), useRef(null)];


  const [inputValues, setInputValues] = useState(["", "", "", ""]);


  const handleInputChange = (index, value) => {

    const newInputValues = [...inputValues];
    newInputValues[index] = value;
    setInputValues(newInputValues);

    if (value.length === 1 && index < inputRefs.length - 1) {
      inputRefs[index + 1].current.focus();
    }
  };
  return (
    <div className="px-20 py-12">
      <h1 className="w-full pb-4 text-2xl font-bold border-b border-[#ACACAC]">
        Payment
      </h1>
      <div className="payment-options flex flex-col">
        <h2 className="font-bold text-base pt-4">Pay with:</h2>
        <div className="card-options flex gap-4">
          <div className="card flex gap-1">
            <input
              type="radio"
              name="paymentOption"
              id="card"
              value="card"
              checked={true}
            />
            <label htmlFor="card">Card</label>
          </div>
        </div>

        <form action="" className="card py-4">
          <div className="card-details flex flex-col gap-4">
            <h1 className="text-center font-medium">Enter the four digit card pin to confirm this payment</h1>
            <div className="flex w-full px-16 gap-12 pb-52">
              {inputValues.map((value, index) => (
                <input
                  key={index}
                  type="number"
                  className="w-1/4 border-[#acacac] border rounded-md py-4 text-center"
                  value={value}
                  onChange={(e) => handleInputChange(index, e.target.value)}
                  ref={inputRefs[index]}
                  maxLength={1}
                  style={{
                    WebkitAppearance: "none",
                    appearance: "none",
                    margin: 0,
                  }}
                />
              ))}
            </div>
          </div>
          <div className="flex flex-col gap-8">
            <button
              onClick={handleClick}
              className="w-full px-12 tracking-wider py-3 bg-black text-white rounded-lg transition duration-300 ease-in-out transform hover:scale-[103%] active:scale-[95%]"
            >
              Confirm Payment
            </button>
            <small className="text-base text-[#acacac]">
              Your personal data will be used to process your order, support
              your experience throughout this website, and for other purposes
              described in our privacy policy
            </small>
          </div>
        </form>
      </div>
    </div>
  );
};

export default PaymentConfirmation;
