import React, { useState } from "react";
import WestBhutanContent from "../components/WestBhutanContent";
import CentralBhutanContent from "../components/CentralBhutanContent";
import SouthBhutanContent from "../components/SouthBhutanContent";
import EastBhutanContent from "../components/EastBhutanContent";

const Guide = ({ chatbotVisible }) => {
  const [activeSection, setActiveSection] = useState("west");

  const handleOptionChange = (section) => {
    setActiveSection(section);
  };

  return (
    <div className="relative w-full">
      <div
        className={`bg-gray-200 p-4 fixed left-0 ${
          chatbotVisible ? "w-[60%]" : "w-[100%]"
        } z-40 pr-[5%] -mt-5 transition-all duration-300`}
      >
        <div className="flex items-center justify-center space-x-10">
          <a
            href="#west"
            onClick={() => handleOptionChange("west")}
            className={`hover:text-black ${
              activeSection === "west" ? "font-bold text-black" : ""
            }`}
          >
            West Bhutan
          </a>
          <a
            href="#central"
            onClick={() => handleOptionChange("central")}
            className={`hover:text-black ${
              activeSection === "central" ? "font-bold text-black" : ""
            }`}
          >
            Central Bhutan
          </a>
          <a
            href="#east"
            onClick={() => handleOptionChange("east")}
            className={`hover:text-black ${
              activeSection === "east" ? "font-bold text-black" : ""
            }`}
          >
            East Bhutan
          </a>
          <a
            href="#south"
            onClick={() => handleOptionChange("south")}
            className={`hover:text-black ${
              activeSection === "south" ? "font-bold text-black" : ""
            }`}
          >
            South Bhutan
          </a>
        </div>
      </div>

      {activeSection === "west" && <WestBhutanContent />}
      {activeSection === "central" && <CentralBhutanContent />}
      {activeSection === "south" && <SouthBhutanContent />}
      {activeSection === "east" && <EastBhutanContent />}
    </div>
  );
};

export default Guide;
