import { useLocation } from "react-router-dom";

const Summary = () => {
  const location = useLocation();
  const bookingDetails = location.state || {};

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold">Booking Summary</h1>
      <p><strong>Check-In:</strong> {bookingDetails.checkInDate?.toLocaleDateString()}</p>
      <p><strong>Check-Out:</strong> {bookingDetails.checkOutDate?.toLocaleDateString()}</p>
      <p><strong>Adults:</strong> {bookingDetails.adults}</p>
      <p><strong>Children:</strong> {bookingDetails.children}</p>
      <p><strong>Guest Type:</strong> {bookingDetails.guestType}</p>
      <p><strong>Price per Night:</strong> Nu.{bookingDetails.pricePerNight}</p>
    </div>
  );
};

export default Summary;
