import React, { useState } from "react";

const TravelPage = ({ chatbotVisible }) => {
  const [isFormVisible, setFormVisible] = useState(false);
  const [isRentalVisible, setRentalVisible] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [isBooked, setIsBooked] = useState(false);
  const [isVehicleBooked, setIsVehicleBooked] = useState(false);

  const handleButtonClick = () => {
    setFormVisible(true);
  };

  const handleCloseClick = () => {
    setFormVisible(false);
  };

  const handleRentalClick = () => {
    setRentalVisible(true);
  };

  const handleRentalClose = () => {
    setRentalVisible(false);
  };

  const handlePayment = () => {
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false);
      setIsBooked(true);
    }, 3000); // Simulate a 3-second delay for the payment process
  };

  const handleBook = () => {
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false);
      setIsVehicleBooked(true);
    }, 3000); // Simulate a 3-second delay for the payment process
  };

  return (
    <div className="">
      <div className="bg-white rounded-lg shadow-md mb-[7rem] relative">
        {isFormVisible ? (
          <button onClick={handleCloseClick} className="absolute top-0 right-0">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 24 24"
              fill="none"
              stroke="white"
              strokeWidth="1"
              strokeLinecap="round"
              strokeLinejoin="round"
              className="w-12 h-12"
            >
              <line x1="15" y1="9" x2="9" y2="15" />
              <line x1="9" y1="9" x2="15" y2="15" />
            </svg>
          </button>
        ) : (
          ""
        )}

        <div
          className={`background overflow-hidden rounded-lg transition-all duration-800 ${
            isFormVisible ? "h-[75vh]" : "h-[50vh]"
          } `}
        >
          <img
            src="https://images.unsplash.com/photo-1613638377394-281765460baa?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1965&q=80"
            alt=""
          />
        </div>
        {/* Taxi Booking Form */}
        <div
          className={`book absolute transition-all duration-500 top-1/2 transform -translate-y-1/2 ${
            isFormVisible ? "right-[10%] opacity-100" : "right-[0] opacity-0"
          } `}
        >
          <form
            action=""
            className="flex flex-col justify-center items-center gap-10"
          >
            <div className="flex justify-center items-center gap-5">
              <label for="pickup" class="text-white">
                Pickup
              </label>
              <input
                type="text"
                id="pickup"
                placeholder="Enter pickup point"
                class="bg-transparent px-4 py-2 border rounded-md text-white border-white focus:outline-none focus:border-black transition-all duration-300"
              />

              <label for="drop" class="text-white">
                Drop
              </label>
              <input
                type="text"
                id="drop"
                placeholder="Enter drop point"
                class="bg-transparent px-4 py-2 border rounded-md text-white border-white focus:outline-none focus:border-black transition-all duration-300"
              />
            </div>

            <button
              className={`${
                isBooked
                  ? "bg-green-600"
                  : "bg-black/[80%] hover:bg-black/[100%]"
              } w-full text-white rounded-md mt-4 tracking-wide p-3 px-5 text-sm transition-all duration-500 ${
                isLoading ? "opacity-50 cursor-not-allowed" : ""
              }`}
              onClick={handlePayment}
              disabled={isLoading}
            >
              {isLoading ? (
                <div className="flex items-center justify-center space-x-2">
                  <div className="animate-spin h-5 w-5 border-t-2 border-b-2 border-blue-800 rounded-full" />
                  <span>Loading...</span>
                </div>
              ) : isBooked ? (
                "Taxi has been booked"
              ) : (
                "Call the Taxi"
              )}
            </button>
          </form>
        </div>

        <div
          className={`left-10 absolute transition-all duration-500  ${
            isFormVisible ? "top-0" : "bottom-[20%]"
          } `}
        >
          <div
            className={`text-left text-white ${
              isFormVisible
                ? "flex translate-y-1/2 bg-black/75 rounded-md justify-start px-4 py-2 items-end gap-4"
                : ""
            } `}
          >
            <h2 className="text-4xl font-semibold tracking-tight">
              Book a Taxi
            </h2>
            <p className="tracking-wide">for your travel needs.</p>
          </div>
          <button
            className={`bg-black/[80%] text-white rounded-full mt-10 hover:bg-black/[100%] tracking-wide ${
              isFormVisible
                ? "p-3 px-5 text-sm transition-all duration-500"
                : "p-4 px-8 transition-all duration-500"
            }`}
            onClick={handleButtonClick}
          >
            Book Now
          </button>
        </div>
      </div>

      {/* Rental Booking Form */}
      <div className="bg-white rounded-lg shadow-md mb-[7rem] relative">
        {isRentalVisible ? (
          <button
            onClick={handleRentalClose}
            className="absolute top-0 right-0"
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 24 24"
              fill="none"
              stroke="white"
              strokeWidth="1"
              strokeLinecap="round"
              strokeLinejoin="round"
              className="w-12 h-12"
            >
              <line x1="15" y1="9" x2="9" y2="15" />
              <line x1="9" y1="9" x2="15" y2="15" />
            </svg>
          </button>
        ) : (
          ""
        )}

        <div
          className={`background overflow-hidden rounded-lg transition-all duration-800 ${
            isRentalVisible ? "h-[75vh]" : "h-[50vh]"
          } `}
        >
          <img
            src="https://images.unsplash.com/photo-1516733968668-dbdce39c4651?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1856&q=80"
            alt=""
          />
        </div>

        <div
          className={`book absolute transition-all duration-500 top-1/2 transform -translate-y-1/2 ${
            isRentalVisible ? "right-[10%] opacity-100" : "right-[0] opacity-0"
          } `}
        >
          <form
            action=""
            className="flex flex-col justify-center items-center gap-10"
          >
            <div className="flex justify-center items-center gap-5">
              <label for="pickup" class="text-white">
                Pickup
              </label>
              <input
                type="text"
                id="pickup"
                placeholder="Enter pickup point"
                class="bg-transparent px-4 py-2 border rounded-md text-white border-white focus:outline-none focus:border-black transition-all duration-300"
              />

              <label for="drop" class="text-white">
                Drop
              </label>
              <input
                type="text"
                id="drop"
                placeholder="Enter drop point"
                class="bg-transparent px-4 py-2 border rounded-md text-white border-white focus:outline-none focus:border-black transition-all duration-300"
              />
            </div>

            <button
              className={`${
                isVehicleBooked
                  ? "bg-green-600"
                  : "bg-black/[80%] hover:bg-black/[100%]"
              } w-full text-white rounded-md mt-4 tracking-wide p-3 px-5 text-sm transition-all duration-500 ${
                isLoading ? "opacity-50 cursor-not-allowed" : ""
              }`}
              onClick={handleBook}
              disabled={isLoading}
            >
              {isLoading ? (
                <div className="flex items-center justify-center space-x-2">
                  <div className="animate-spin h-5 w-5 border-t-2 border-b-2 border-blue-800 rounded-full" />
                  <span>Loading...</span>
                </div>
              ) : isVehicleBooked ? (
                "Vehicle has been booked"
              ) : (
                "Book a vehicle"
              )}
            </button>
          </form>
        </div>

        <div
          className={`left-10 absolute transition-all duration-500  ${
            isRentalVisible ? "top-0" : "bottom-[20%]"
          } `}
        >
          <div
            className={`text-left text-white ${
              isRentalVisible
                ? "flex translate-y-1/2 bg-black/75 rounded-md justify-start px-4 py-2 items-end gap-4"
                : ""
            } `}
          >
            <h2 className="text-4xl font-semibold tracking-tight">
              Rent a Vehicle
            </h2>
            <p className="tracking-wide">for your perfect trip</p>
          </div>
          <button
            className={`bg-black/[80%] text-white rounded-full mt-10 hover:bg-black/[100%] tracking-wide ${
              isRentalVisible
                ? "p-3 px-5 text-sm transition-all duration-500"
                : "p-4 px-8 transition-all duration-500"
            }`}
            onClick={handleRentalClick}
          >
            Book Now
          </button>
        </div>
      </div>
    </div>
  );
};

export default TravelPage;
