import React, { useState } from "react";
import { Link } from "react-router-dom";

const Availability = ({ chatbotVisible }) => {
  const [adults, setAdults] = useState(1);
  const [children, setChildren] = useState(0);
  const [guestType, setGuestType] = useState("International");

  const handleAdultChange = (type) => {
    setAdults((prev) => (type === "increment" ? prev + 1 : prev > 1 ? prev - 1 : 1));
  };

  const handleChildrenChange = (type) => {
    setChildren((prev) => (type === "increment" ? prev + 1 : prev > 0 ? prev - 1 : 0));
  };

  return (
    <div
      className={`right checkout ${
        chatbotVisible ? "w-[40%] mt-10" : "w-[40%] ml-[10%]"
      }`}
    >
      <div className="flex flex-col gap-6 shadow-lg p-6 rounded-lg bg-white">
        {/* Price Section */}
        <div className="heading flex items-end justify-between">
          <div className="price flex items-end">
            <h1 className="text-2xl font-semibold">Nu.3000</h1>
            <p className="text-[#6b727f]"> / night</p>
          </div>
        </div>

        {/* Check-in & Check-out Section */}
        <div className="flex flex-col border border-gray-300 rounded-lg">
          <div className="flex justify-between items-start w-full border-b border-gray-300">
            <div className="w-full border-r border-gray-300 p-3">
              <h3 className="uppercase font-bold text-[0.8rem] flex items-center gap-1">
                🗓️ Check-In
              </h3>
              <p className="text-[#6b727f]">12/02/2025</p>
            </div>
            <div className="w-full p-3">
              <h3 className="uppercase font-bold text-[0.8rem] flex items-center gap-1">
                🗓️ Check-Out
              </h3>
              <p className="text-[#6b727f]">14/02/2025</p>
            </div>
          </div>

          {/* Adults Selection */}
          <div className="flex justify-between items-center border-b border-gray-300 p-3">
            <div className="flex items-center gap-2">
              <span className="text-lg">🧑</span>
              <h3 className="uppercase font-bold text-[0.8rem]">Adults</h3>
            </div>
            <div className="flex gap-2 items-center">
              <button
                onClick={() => handleAdultChange("increment")}
                className="w-8 h-8 bg-[#f7f7f7] rounded-lg flex justify-center items-center hover:bg-[#e2e2e2]"
              >
                ➕
              </button>
              <span className="text-lg font-semibold">{adults}</span>
              <button
                onClick={() => handleAdultChange("decrement")}
                className="w-8 h-8 bg-[#f7f7f7] rounded-lg flex justify-center items-center hover:bg-[#e2e2e2]"
              >
                ➖
              </button>
            </div>
          </div>

          {/* Children Selection */}
          <div className="flex justify-between items-center border-b border-gray-300 p-3">
            <div className="flex items-center gap-2">
              <span className="text-lg">👶</span>
              <h3 className="uppercase font-bold text-[0.8rem]">Children</h3>
            </div>
            <div className="flex gap-2 items-center">
              <button
                onClick={() => handleChildrenChange("increment")}
                className="w-8 h-8 bg-[#f7f7f7] rounded-lg flex justify-center items-center hover:bg-[#e2e2e2]"
              >
                ➕
              </button>
              <span className="text-lg font-semibold">{children}</span>
              <button
                onClick={() => handleChildrenChange("decrement")}
                className="w-8 h-8 bg-[#f7f7f7] rounded-lg flex justify-center items-center hover:bg-[#e2e2e2]"
              >
                ➖
              </button>
            </div>
          </div>

          {/* Guest Type Dropdown */}
          <div className="p-3">
            <h3 className="uppercase font-bold text-[0.8rem] flex items-center gap-1">
              📱 Guest Type
            </h3>
            <select
              value={guestType}
              onChange={(e) => setGuestType(e.target.value)}
              className="w-full border rounded-lg p-2 mt-1 text-gray-700"
            >
              <option>International</option>
              <option>Local</option>
            </select>
          </div>
        </div>

        {/* Book Now Button */}
        <div className="reserve_button">
          <Link to="/room/id/payment">
            <button className="w-full tracking-wider py-3 bg-[#FF6600] text-white rounded-lg transition duration-300 ease-in-out transform hover:bg-black/80 active:scale-[95%]">
              BOOK NOW
            </button>
          </Link>
        </div>
      </div>
    </div>
  );
};

