import logo from "./assets/svg/logo_black.svg";
import { Routes, Route } from "react-router-dom";
import Layout from "./components/Layout";
import Home from "./pages/Home";
import Homestay from "./pages/Homestay";
import { useState } from "react";
import Hotel from "./pages/Hotel";
import Rooms from "./pages/Rooms";
import Contact from "./pages/Contact";
import Flightbook from "./pages/Flightbook";
import Packages from "./pages/Packages";
import Restaurant from "./pages/Restaurant";
import Checkout from "./pages/Checkout";
import Travel from "./pages/Travel";
import Payment from "./pages/Payment";
import Error from "./pages/Error";
import Success from "./pages/Success";

function App() {
  const [chatbotVisible, setChatbotVisible] = useState(false);

  const toggleChatbot = () => {
    setChatbotVisible(!chatbotVisible);
  };

  return (
    <Routes>
      <Route
        path="/"
        element={
          <Layout onSearch={toggleChatbot} chatbotVisible={chatbotVisible} />
        }
      >
        <Route index element={<Home chatbotVisible={chatbotVisible} />} />
        <Route
          path="homestay"
          element={<Homestay chatbotVisible={chatbotVisible} />}
        />
        <Route
          path="homestay/search"
          element={<Homestay chatbotVisible={chatbotVisible} />}
        />
        <Route
          path="hotels"
          element={<Hotel chatbotVisible={chatbotVisible} />}
        />
        <Route
          path="hotels/search"
          element={<Hotel chatbotVisible={chatbotVisible} />}
        />
        <Route
          path="flightbook"
          element={<Flightbook chatbotVisible={chatbotVisible} />}
        />
        <Route
          path="travel"
          element={<Travel chatbotVisible={chatbotVisible} />}
        />
        <Route
          path="restaurant"
          element={<Restaurant chatbotVisible={chatbotVisible} />}
        />
        <Route
          path="packages"
          element={<Packages chatbotVisible={chatbotVisible} />}
        />
        <Route
          path="room"
          element={<Rooms chatbotVisible={chatbotVisible} />}
        />
        <Route
          path="checkout"
          element={<Checkout chatbotVisible={chatbotVisible} />}
        />
        <Route
          path="contact"
          element={<Contact chatbotVisible={chatbotVisible} />}
        />
      </Route>
      <Route path="*" element={<Error />} />
      <Route path="/room/id/payment" element={<Payment />} />
      <Route path="/room/id/payment/success" element={<Success />} />
    </Routes>
  );
}

export default App;
