import { SignUp, useUser } from "@clerk/clerk-react";
import { useEffect } from "react";
import { supabase } from "../../utils/supabaseClient";

function HostSignup() {
  const { user } = useUser();

  useEffect(() => {
    if (user) {
      console.log("User from Clerk:", user);  // ✅ Debugging
      saveHostToSupabase(user);
    }
  }, [user]);

  async function saveHostToSupabase(user) {
    const { data, error } = await supabase.from("hosts").insert([
      {
        clerk_user_id: user.id,
        host_name: user.fullName,
        host_email: user.primaryEmailAddress?.emailAddress, // Fix for email retrieval
      }
    ]);

    if (error) {
      console.error("Error saving host:", error);  // ✅ Debugging
    } else {
      console.log("Host saved successfully:", data);  // ✅ Debugging
    }
  }

  return (
    <div className="flex justify-center items-center h-screen">
      <SignUp routing="path" path="/host/signup" />
    </div>
  );
}

export default HostSignup;
