import React, { useEffect, useState } from "react";
import { useUser } from "@clerk/clerk-react";
import { supabase } from "../../utils/supabaseClient";
import Sidebar from "../../components/Sidebar";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const dzongkhags = [
  "Bumthang", "Chhukha", "Dagana", "Gasa", "Haa", "Lhuentse", "Mongar", 
  "Paro", "Pema Gatshel", "Punakha", "Samdrup Jongkhar", "Samtse", "Sarpang",
  "Thimphu", "Trashi Yangtse", "Trashigang", "Trongsa", "Tsirang", "Wangdue Phodrang", "Zhemgang"
];

function Homestay() {
  const { user } = useUser();
  const [homestay, setHomestay] = useState(null);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    facilities: "",
    availabilitydate: [],
    location: "",
    googlemapurl: "",
    images: [], // Changed to array for multiple images
    pricepernight: "",
  });

  useEffect(() => {
    if (user) {
      fetchHomestay();
    }
  }, [user]);

  async function fetchHomestay() {
    setLoading(true);
    try {
      const { data: hostData, error: hostError } = await supabase
        .from("host")
        .select("hostid")
        .eq("clerkuserid", user.id)
        .single();

      if (hostError || !hostData) {
        console.error("Error fetching host ID:", hostError);
        setLoading(false);
        return;
      }

      const hostId = hostData.hostid;

      const { data, error } = await supabase
        .from("homestay")
        .select("*")
        .eq("hostid", hostId)
        .single();

      if (error) {
        console.error("Error fetching homestay:", error);
      } else {
        console.log("Fetched homestay:", data);
        setHomestay(data);
      }
    } catch (err) {
      console.error("Unexpected error:", err);
    }
    setLoading(false);
  }

  /** Handle Image Upload */
  async function handleImageUpload(event) {
    const file = event.target.files[0];
    if (!file) return;

    const fileName = `${user.id}-${Date.now()}-${file.name}`; // Unique filename
    const { data, error } = await supabase.storage
      .from("homestay-images") // Ensure "homestay-images" bucket exists in Supabase Storage
      .upload(fileName, file);

    if (error) {
      console.error("Error uploading image:", error);
      return;
    }

    const { data: publicUrl } = supabase.storage.from("homestay-images").getPublicUrl(fileName);

    setFormData(prevState => ({
      ...prevState,
      images: [...prevState.images, publicUrl.publicUrl], // Append image URL to list
    }));

    console.log("Uploaded Image URL:", publicUrl.publicUrl);
  }

  async function handleSubmit(e) {
    e.preventDefault();
    if (!user) return;

    const { data: hostData, error: hostError } = await supabase
      .from("host")
      .select("hostid")
      .eq("clerkuserid", user.id)
      .single();

    if (hostError || !hostData) {
      console.error("Error fetching host ID:", hostError);
      return;
    }

    const hostId = hostData.hostid;

    const { error } = await supabase.from("homestay").insert([
      {
        hostid: hostId,
        name: formData.name,
        description: formData.description,
        facilities: formData.facilities,
        availabilitydate: formData.availabilitydate.map(date => date.toISOString().split("T")[0]),
        location: formData.location,
        googlemapurl: formData.googlemapurl,
        images: formData.images, // Store uploaded image URLs
        pricepernight: formData.pricepernight,
      },
    ]);

    if (error) {
      console.error("Error adding homestay:", error);
    } else {
      console.log("Homestay added successfully!");
      fetchHomestay();
    }
  }

  return (
    <div className="flex h-screen">
      <Sidebar />
      <main className="flex-1 p-8">
        <h2 className="text-2xl font-semibold">Homestay Management</h2>

        {homestay ? (
          <div className="mt-6">
            <h3 className="text-xl font-semibold">{homestay.name}</h3>
            <p className="text-gray-600 mt-2">{homestay.description}</p>
            <p className="text-gray-600 mt-2"><strong>Facilities:</strong> {homestay.facilities}</p>
            <p className="text-gray-600 mt-2"><strong>Availability Dates:</strong> {homestay.availabilitydate.join(", ")}</p>
            <p className="text-gray-600 mt-2"><strong>Location:</strong> {homestay.location}</p>
            <p className="text-gray-600 mt-2"><strong>Google Map:</strong> <a href={homestay.googlemapurl} target="_blank" rel="noopener noreferrer">View on Google Maps</a></p>
            <p className="text-gray-600 mt-2"><strong>Price per Night:</strong> Nu. {homestay.pricepernight}</p>
            <div className="mt-4">
              <h4 className="text-lg font-semibold">Images</h4>
              <div className="flex gap-2 mt-2">
                {homestay.images.map((img, index) => (
                  <img key={index} src={img} alt="Homestay" className="w-32 h-32 object-cover rounded-lg" />
                ))}
              </div>
            </div>
          </div>
        ) : (
          <div className="mt-6">
            <p className="text-gray-600">You have not listed any homestay yet. Add your homestay below:</p>
            <form onSubmit={handleSubmit} className="mt-4 space-y-4">
              <input type="text" name="name" placeholder="Homestay Name" className="w-full border p-2 rounded" value={formData.name} onChange={e => setFormData({ ...formData, name: e.target.value })} required />

              <input type="file" onChange={handleImageUpload} className="w-full border p-2 rounded" accept="image/*" />

              <div className="flex gap-2 mt-2">
                {formData.images.map((img, index) => (
                  <img key={index} src={img} alt="Homestay" className="w-20 h-20 object-cover rounded-lg" />
                ))}
              </div>

              <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded">Add Homestay</button>
            </form>
          </div>
        )}
      </main>
    </div>
  );
}

export default Homestay;
