import React from "react";
import { useParams } from "react-router-dom";

const HomestayDetails = () => {
  const { district } = useParams();

  return (
    <div className="max-w-4xl mx-auto p-6">
      <img
        src="https://via.placeholder.com/800x400" // üî• Demo Image
        alt="Homestay Demo"
        className="w-full h-64 object-cover rounded-lg mb-4"
      />
      <h1 className="text-3xl font-bold">Sample Homestay</h1>
      <p className="text-gray-600">Location: {district.charAt(0).toUpperCase() + district.slice(1)}</p>
      <p className="text-lg text-gray-800 mt-2">
        This is a demo page for showcasing homestay details. You can replace this with real homestay data.
      </p>
      <p className="text-lg font-semibold mt-4">Nu. 2500 / night</p>
      <p className="text-yellow-500 font-semibold mt-2">‚≠ê 4.8</p>
    </div>
  );
};

export default HomestayDetails;
