import React, { useEffect, useState } from "react";
import { supabase } from "../../utils/supabaseClient";
import Sidebar from "../../components/Sidebar";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const dzongkhags = [
  "Bumthang", "Chhukha", "Dagana", "Gasa", "Haa", "Lhuentse", "Mongar", 
  "Paro", "Pema Gatshel", "Punakha", "Samdrup Jongkhar", "Samtse", "Sarpang",
  "Thimphu", "Trashi Yangtse", "Trashigang", "Trongsa", "Tsirang", "Wangdue Phodrang", "Zhemgang"
];

function Homestay() {
  const [user, setUser] = useState(null);
  const [homestay, setHomestay] = useState(null);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    facilities: "",
    numbedroom: "",
    numbed: "",
    availabilitydate: [],
    location: "",
    googlemapurl: "",
    images: [],
    pricepernight: "",
  });
  const [imageFiles, setImageFiles] = useState([]);

  useEffect(() => {
    async function fetchUser() {
      const { data, error } = await supabase.auth.getUser();
      if (error) {
        console.error("Error fetching user:", error);
      } else {
        setUser(data.user);
        fetchHomestay(data.user.id); 
      }
    }
    fetchUser();
  }, []);

  async function fetchHomestay(userId) {
    setLoading(true);
    try {
      const { data: hostData, error: hostError } = await supabase
        .from("host")
        .select("hostid")
        .eq("hostid", userId)
        .single();

      if (hostError || !hostData) {
        console.error("Error fetching host ID:", hostError);
        setLoading(false);
        return;
      }

      const hostId = hostData.hostid;

      const { data, error } = await supabase
        .from("homestay")
        .select("*")
        .eq("hostid", hostId)
        .single();

      if (error) {
        console.error("Error fetching homestay:", error);
      } else {
        setHomestay(data);
      }
    } catch (err) {
      console.error("Unexpected error:", err);
    }
    setLoading(false);
  }

  function handleChange(e) {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  }

  function handleDateChange(date) {
    if (!date) return;
    setFormData(prevState => ({
      ...prevState,
      availabilitydate: [...prevState.availabilitydate, date]
    }));
  }

  async function handleSubmit(e) {
    e.preventDefault();
    if (!user) return;

    const { data: hostData, error: hostError } = await supabase
      .from("host")
      .select("hostid")
      .eq("hostid", user.id) 
      .single();

    if (hostError || !hostData) {
      console.error("Error fetching host ID:", hostError);
      return;
    }

    const hostId = hostData.hostid;

    let imageUrls = [];

    for (const file of imageFiles) {
      const { data, error } = await supabase.storage
        .from("homestay-images")
        .upload(`homestays/${user.id}/${file.name}`, file, { cacheControl: "3600" });

      if (error) {
        console.error("Error uploading image:", error);
      } else {
        console.log("Uploaded file path:", data.path);

        const { data: publicURL } = supabase.storage
          .from("homestay-images")
          .getPublicUrl(data.path);

        console.log("Generated Public URL:", publicURL.publicUrl);
        imageUrls.push(publicURL.publicUrl);
      }
    }

    console.log("Final image URLs:", imageUrls);

    const { error } = await supabase.from("homestay").insert([
      {
        hostid: hostId,
        name: formData.name,
        description: formData.description,
        facilities: formData.facilities,
        availabilitydate: formData.availabilitydate.map(date => date.toISOString().split("T")[0]),
        location: formData.location,
        googlemapurl: formData.googlemapurl,
        numbedroom: formData.numbedroom,
        numbed: formData.numbed,
        images: imageUrls,
        pricepernight: formData.pricepernight,
      },
    ]);

    if (error) {
      console.error("Error adding homestay:", error);
    } else {
      console.log("Homestay added successfully!");
      fetchHomestay(user.id);
    }
  }

  if (loading) return <div className="flex justify-center items-center h-screen">Loading...</div>;

  return (
    <div className="flex h-screen">
      <Sidebar />
      <main className="flex-1 p-8">
        <h2 className="text-2xl font-semibold">Homestay Management</h2>

        {/* ‚úÖ Display Homestay if Exists */}
        {homestay ? (
          <div className="bg-white shadow-lg p-6 rounded-lg">
            <h3 className="text-xl font-semibold">{homestay.name}</h3>
            <p><strong>Host Name:</strong> {user?.full_name || "N/A"}</p>
            <p><strong>Google Map Location:</strong> <a href={homestay.googlemapurl} target="_blank" className="text-blue-500">{homestay.googlemapurl}</a></p>
            <p><strong>No of bedrooms:</strong> {homestay.numbedroom} &nbsp;&nbsp; <strong>No of beds:</strong> {homestay.numbed}</p>
            <p><strong>Price per night (Nu.):</strong> {homestay.pricepernight}</p>
            <p><strong>Facilities:</strong> {homestay.facilities}</p>
            <p><strong>Description:</strong> {homestay.description}</p>
            <p><strong>Availability:</strong> {homestay.availabilitydate}</p>

            {/* ‚úÖ Display Images in Grid */}
            {homestay.images.length > 0 && (
              <div className="mt-4 grid grid-cols-3 gap-4">
                {homestay.images.map((img, index) => (
                  <img key={index} src={img} alt={`Homestay ${index + 1}`} className="w-full h-32 object-cover rounded-lg" />
                ))}
              </div>
            )}
          </div>
        ) : (
          // ‚úÖ If No Homestay, Show Form
          <form onSubmit={handleSubmit} className="mt-4 space-y-4">
            <input type="text" name="name" placeholder="Homestay Name" className="w-full border p-2 rounded" value={formData.name} onChange={handleChange} required />
            <input type="number" name="numbedroom" placeholder="No of bedrooms" className="w-full border p-2 rounded" value={formData.numbedroom} onChange={handleChange} required />
            <input type="number" name="numbed" placeholder="No of beds" className="w-full border p-2 rounded" value={formData.numbed} onChange={handleChange} required />
            <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded">Add Homestay</button>
          </form>
        )}
      </main>
    </div>
  );
}

export default Homestay;
