import React, { useEffect, useState } from "react";
import { useUser } from "@clerk/clerk-react";
import { supabase } from "../../utils/supabaseClient";
import Sidebar from "../../components/Sidebar";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const dzongkhags = [
  "Bumthang", "Chhukha", "Dagana", "Gasa", "Haa", "Lhuentse", "Mongar", 
  "Paro", "Pema Gatshel", "Punakha", "Samdrup Jongkhar", "Samtse", "Sarpang",
  "Thimphu", "Trashi Yangtse", "Trashigang", "Trongsa", "Tsirang", "Wangdue Phodrang", "Zhemgang"
];

function Homestay() {
  const { user } = useUser();
  const [homestay, setHomestay] = useState(null);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    facilities: "",
    availabilitydate: [],
    location: "",
    googlemapurl: "",
    images: [],
    pricepernight: "",
  });

  useEffect(() => {
    if (user) {
      fetchHomestay();
    }
  }, [user]);

  async function fetchHomestay() {
    setLoading(true);
    try {
      const { data: hostData, error: hostError } = await supabase
        .from("host")
        .select("hostid")
        .eq("clerkuserid", user.id)
        .single();

      if (hostError || !hostData) {
        console.error("Error fetching host ID:", hostError);
        setLoading(false);
        return;
      }

      const hostId = hostData.hostid;

      const { data, error } = await supabase
        .from("homestay")
        .select("*")
        .eq("hostid", hostId)
        .maybeSingle();  // Avoids error if no data is found

      if (error) {
        console.error("Error fetching homestay:", error);
      } else {
        console.log("Fetched homestay:", data);
        setHomestay(data);

        if (data) {
          setFormData({
            name: data.name || "",
            description: data.description || "",
            facilities: data.facilities || "",
            availabilitydate: data.availabilitydate.map(d => new Date(d)) || [], // Convert back to Date objects
            location: data.location || "",
            googlemapurl: data.googlemapurl || "",
            images: data.images || [],
            pricepernight: data.pricepernight || "",
          });
        }
      }
    } catch (err) {
      console.error("Unexpected error:", err);
    }
    setLoading(false);
  }

  async function handleImageUpload(event) {
    const file = event.target.files[0];
    if (!file) return;

    const fileName = `${user.id}-${Date.now()}-${file.name}`;

    const { data, error } = await supabase.storage
      .from("images") // Ensure this matches your bucket name
      .upload(fileName, file);

    if (error) {
      console.error("Error uploading image:", error);
      return;
    }

    const { data: publicUrl } = supabase.storage
      .from("images")
      .getPublicUrl(fileName);

    setFormData(prevState => ({
      ...prevState,
      images: [...prevState.images, publicUrl.publicUrl],
    }));

    console.log("Uploaded Image URL:", publicUrl.publicUrl);
  }


  function handleChange(e) {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  }

  function handleDateChange(date) {
    if (!date) return;

    // Convert the selected date to a Date object before storing
    setFormData(prevState => ({
      ...prevState,
      availabilitydate: [...prevState.availabilitydate, new Date(date)],
    }));
  }


  function removeDate(index) {
    setFormData(prevState => ({
      ...prevState,
      availabilitydate: prevState.availabilitydate.filter((_, i) => i !== index),
    }));
  }

  async function handleSubmit(e) {
    e.preventDefault();
    if (!user) return;

    const { data: hostData, error: hostError } = await supabase
      .from("host")
      .select("hostid")
      .eq("clerkuserid", user.id)
      .single();

    if (hostError || !hostData) {
      console.error("Error fetching host ID:", hostError);
      return;
    }

    const hostId = hostData.hostid;

    // Ensure valid Date conversion
    const formattedDates = formData.availabilitydate.map(date => 
      date instanceof Date ? date.toISOString().split("T")[0] : date
    );

    const { error } = await supabase.from("homestay").insert([
      {
        hostid: hostId,
        name: formData.name,
        description: formData.description,
        facilities: formData.facilities,
        availabilitydate: formattedDates, // Now correctly formatted
        location: formData.location,
        googlemapurl: formData.googlemapurl,
        images: formData.images,
        pricepernight: formData.pricepernight,
      },
    ]);

    if (error) {
      console.error("Error adding homestay:", error);
    } else {
      console.log("Homestay added successfully!");
      fetchHomestay();
    }
  }


  return (
    <div className="flex h-screen">
      <Sidebar />
      <main className="flex-1 p-8">
        <h2 className="text-2xl font-semibold">Homestay Management</h2>

        <form onSubmit={handleSubmit} className="mt-4 space-y-4">
          <input type="text" name="name" placeholder="Homestay Name" className="w-full border p-2 rounded" value={formData.name} onChange={handleChange} required />
          <textarea name="description" placeholder="Description" className="w-full border p-2 rounded" value={formData.description} onChange={handleChange} required />
          <input type="text" name="facilities" placeholder="Facilities" className="w-full border p-2 rounded" value={formData.facilities} onChange={handleChange} required />

          {/* Multi-Date Availability Selection */}
          <div>
            <label className="block text-gray-700 font-semibold">Availability Dates:</label>
            <DatePicker selected={null} onChange={handleDateChange} dateFormat="yyyy-MM-dd" className="w-full border p-2 rounded" placeholderText="Select available dates" />
            <div className="mt-2 flex flex-wrap gap-2">
              {formData.availabilitydate.map((date, index) => (
                <div key={index} className="flex items-center bg-gray-200 px-2 py-1 rounded">
                  <span>{date.toISOString().split("T")[0]}</span>
                  <button type="button" className="ml-2 text-red-500" onClick={() => removeDate(index)}>❌</button>
                </div>
              ))}
            </div>
          </div>

          {/* Location Dropdown */}
          <select name="location" value={formData.location} onChange={handleChange} className="w-full border p-2 rounded" required>
            <option value="" disabled>Select Dzongkhag</option>
            {dzongkhags.map((dzongkhag, index) => (
              <option key={index} value={dzongkhag}>{dzongkhag}</option>
            ))}
          </select>

          <input type="text" name="googlemapurl" placeholder="Google Map URL" className="w-full border p-2 rounded" value={formData.googlemapurl} onChange={handleChange} required />
          <input type="number" name="pricepernight" placeholder="Price per Night" className="w-full border p-2 rounded" value={formData.pricepernight} onChange={handleChange} required />
          <input type="file" onChange={handleImageUpload} className="w-full border p-2 rounded" accept="image/*" multiple />

          <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded">Add Homestay</button>
        </form>
      </main>
    </div>
  );
}

export default Homestay;
