import { useParams } from "react-router-dom";

const DistrictPage = () => {
  const { district } = useParams();
  const formattedDistrict = district.replace(/\s+/g, "").toLowerCase(); // Format name to match file names

  // Dynamically import images from assets/dzongkhags folder
  const getImageUrl = (districtName) => {
    try {
      return require(`../assets/dzongkhags/${districtName}.jpg`);
    } catch (error) {
      return require("../assets/dzongkhags/default.jpg"); // Fallback image
    }
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-4">Welcome to {district}</h1>
      <img 
        src={getImageUrl(formattedDistrict)} 
        alt={district} 
        className="w-full h-64 object-cover rounded-lg shadow-lg"
      />
      <p className="mt-4 text-lg">Information about {district} will be displayed here.</p>
    </div>
  );
};

export default DistrictPage;
