import React, { useEffect, useState } from "react";
import { useUser } from "@clerk/clerk-react";
import { supabase } from "../../utils/supabaseClient";
import Sidebar from "../../components/Sidebar";

function Homestay() {
  const { user } = useUser();
  const [homestay, setHomestay] = useState(null);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    location: "",
    price: "",
  });

  useEffect(() => {
    if (user) {
      fetchHomestay();
    }
  }, [user]);

  /** Fetch the homestay for the logged-in host */
  async function fetchHomestay() {
    setLoading(true);
    const { data, error } = await supabase
      .from("homestay")
      .select("*")
      .eq("hostid", user.id)
      .single();

    if (error && error.code !== "PGRST116") {
      console.error("Error fetching homestay:", error);
    } else {
      setHomestay(data);
    }
    setLoading(false);
  }

  /** Handle form input changes */
  function handleChange(e) {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  }

  /** Handle homestay submission */
  async function handleSubmit(e) {
    e.preventDefault();
    if (!user) return;

    const { error } = await supabase.from("homestay").insert([
      {
        hostid: user.id,
        name: formData.name,
        description: formData.description,
        location: formData.location,
        price: formData.price,
      },
    ]);

    if (error) {
      console.error("Error adding homestay:", error);
    } else {
      fetchHomestay(); // Refresh data
    }
  }

  if (loading) return <div className="flex justify-center items-center h-screen">Loading...</div>;

  return (
    <div className="flex h-screen">
      <Sidebar />
      <main className="flex-1 p-8">
        <h2 className="text-2xl font-semibold">Homestay Management</h2>

        {homestay ? (
          <div className="mt-6">
            <h3 className="text-xl font-semibold">{homestay.name}</h3>
            <p className="text-gray-600 mt-2">{homestay.description}</p>
            <p className="text-gray-600 mt-2"><strong>Location:</strong> {homestay.location}</p>
            <p className="text-gray-600 mt-2"><strong>Price per Night:</strong> Nu. {homestay.price}</p>
            <button className="bg-blue-500 text-white px-4 py-2 rounded mt-4">Edit</button>
          </div>
        ) : (
          <div className="mt-6">
            <p className="text-gray-600">You have not listed any homestay yet. Add your homestay below:</p>
            <form onSubmit={handleSubmit} className="mt-4 space-y-4">
              <input
                type="text"
                name="name"
                placeholder="Homestay Name"
                className="w-full border p-2 rounded"
                value={formData.name}
                onChange={handleChange}
                required
              />
              <textarea
                name="description"
                placeholder="Description"
                className="w-full border p-2 rounded"
                value={formData.description}
                onChange={handleChange}
                required
              />
              <input
                type="text"
                name="location"
                placeholder="Location"
                className="w-full border p-2 rounded"
                value={formData.location}
                onChange={handleChange}
                required
              />
              <input
                type="number"
                name="price"
                placeholder="Price per Night (Nu.)"
                className="w-full border p-2 rounded"
                value={formData.price}
                onChange={handleChange}
                required
              />
              <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded">
                Add Homestay
              </button>
            </form>
          </div>
        )}
      </main>
    </div>
  );
}

export default Homestay;
