import { useEffect, useState } from "react";
import { useUser } from "@clerk/clerk-react";
import { useNavigate } from "react-router-dom";
import Sidebar from "../../components/Sidebar";
import { Link } from "react-router-dom";
import { supabase } from "../../utils/supabaseClient";

function HostDashboard() {
  const { user, isSignedIn } = useUser();
  const navigate = useNavigate();
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    if (!isSignedIn) {
      navigate("/host/login");
    } else {
      fetchBookings();
    }
  }, [isSignedIn, navigate]);

  async function fetchBookings() {
    const { data, error } = await supabase
      .from("bookings")
      .select("*, homestays(name)")
      .eq("homestays.host_id", user.id);

    if (error) {
      console.error("Error fetching bookings:", error);
    } else {
      setBookings(data);
    }
  }

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <main className="flex-1 p-8">
        <div className="flex justify-between items-center">
          <h2 className="text-2xl font-semibold">Booking Management</h2>
          <div className="flex items-center space-x-2">
            <span className="font-semibold">{user?.fullName}</span>
          </div>
        </div>

        {/* Booking Table */}
        <table className="w-full bg-white mt-6 overflow-hidden">
          <thead>
            <tr className=" text-gray-500 text-left border-b">
              <th className="py-3 px-4">Name</th>
              <th className="py-3 px-4">Check-in</th>
              <th className="py-3 px-4">Check-out</th>
              <th className="py-3 px-4">Adults</th>
              <th className="py-3 px-4">Children</th>
              <th className="py-3 px-4">Action</th>
            </tr>
          </thead>
          <tbody>
            {bookings.map((booking) => (
              <tr key={booking.booking_id}>
                <td className="py-3 px-4">{bookings.guest_name}</td>
                <td className="py-3 px-4">{booking.check_in}</td>
                <td className="py-3 px-4">{booking.check_out}</td>
                <td className="py-3 px-4">{booking.adult}</td>
                <td className="py-3 px-4">{booking.children}</td>
                <td className="py-3 px-4">
                  <button className="bg-[#51CF03] text-white px-3 py-1 rounded-full hover:bg-green-600 mr-2">Accept</button>
                  <button className="bg-[#FF6600] text-white px-3 py-1 rounded-full hover:bg-red-600">Decline</button>
                </td>
                <Link to={`/booking/${booking.booking_id}`}>
                  <td className="underline text-[#36E6E6E] cursor-pointer py-3 px-4">More details</td>
                </Link>
              </tr>
            ))}
          </tbody>
        </table>
      </main>
    </div>
  );
}

export default HostDashboard;
