import { useEffect } from "react";
import { useUser, SignOutButton } from "@clerk/clerk-react";
import { useNavigate } from "react-router-dom";
import Sidebar from "../../components/Sidebar"

const bookings = [
  { id: 1, name: "John Mathew", checkin: "12/2/2025", checkout: "14/2/2025", adults: 1, children: 0 },
  { id: 2, name: "John Mathew", checkin: "12/2/2025", checkout: "14/2/2025", adults: 1, children: 0 },
];

function HostDashboard() {
  const { user, isSignedIn } = useUser();
  const navigate = useNavigate();

  useEffect(() => {
    if (!isSignedIn) {
      navigate("/host/login");
    }
  }, [isSignedIn, navigate]);

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <Sidebar/>

      {/* Main Content */}
      <main className="flex-1 p-8 bg-gray-100">
        <div className="flex justify-between items-center">
          <h2 className="text-2xl font-semibold">Booking Management</h2>
          <div className="flex items-center space-x-2">
            <span className="font-semibold">{user?.fullName}</span>
          </div>
        </div>

        {/* Booking Table */}
        <table className="w-full bg-white mt-6 shadow-md rounded-lg overflow-hidden">
          <thead>
            <tr className="bg-gray-200 text-gray-700 text-left">
              <th className="py-3 px-4">Name</th>
              <th className="py-3 px-4">Check-in</th>
              <th className="py-3 px-4">Check-out</th>
              <th className="py-3 px-4">Adults</th>
              <th className="py-3 px-4">Children</th>
              <th className="py-3 px-4">Action</th>
            </tr>
          </thead>
          <tbody>
            {bookings.map((booking) => (
              <tr key={booking.id} className="border-t">
                <td className="py-3 px-4">{booking.name}</td>
                <td className="py-3 px-4">{booking.checkin}</td>
                <td className="py-3 px-4">{booking.checkout}</td>
                <td className="py-3 px-4">{booking.adults}</td>
                <td className="py-3 px-4">{booking.children}</td>
                <td className="py-3 px-4">
                  <button className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600 mr-2">Accept</button>
                  <button className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600">Decline</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </main>
    </div>
  );
}

export default HostDashboard;
