import React, { useEffect, useState } from "react";
import { supabase } from "../../utils/supabaseClient";
import Sidebar from "../../components/Sidebar";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const dzongkhags = [
  "Bumthang", "Chhukha", "Dagana", "Gasa", "Haa", "Lhuentse", "Mongar",
  "Paro", "Pema Gatshel", "Punakha", "Samdrup Jongkhar", "Samtse", "Sarpang",
  "Thimphu", "Trashi Yangtse", "Trashigang", "Trongsa", "Tsirang", "Wangdue Phodrang", "Zhemgang"
];

function Homestay() {
  const [user, setUser] = useState(null);
  const [homestay, setHomestay] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    facilities: "",
    numbedrooms: "",
    numbeds: "",
    availabilitydate: [],
    location: "",
    googlemapurl: "",
    images: [],
    pricepernight: "",
  });
  const [imageFiles, setImageFiles] = useState([]);

  useEffect(() => {
    async function fetchUser() {
      const { data, error } = await supabase.auth.getUser();
      if (error) {
        console.error("Error fetching user:", error);
      } else {
        setUser(data.user);
        fetchHomestay(data.user.id);
      }
    }
    fetchUser();
  }, []);

  async function fetchHomestay(userId) {
    setLoading(true);
    try {
      const { data: hostData, error: hostError } = await supabase
        .from("host")
        .select("hostid")
        .eq("hostid", userId)
        .single();

      if (hostError || !hostData) {
        console.error("Error fetching host ID:", hostError);
        setLoading(false);
        return;
      }

      const hostId = hostData.hostid;

      const { data, error } = await supabase
        .from("homestay")
        .select("*")
        .eq("hostid", hostId)
        .single();

      if (error) {
        console.error("Error fetching homestay:", error);
      } else {
        setHomestay(data);
        setFormData({ ...data, availabilitydate: data.availabilitydate || [] });
      }
    } catch (err) {
      console.error("Unexpected error:", err);
    }
    setLoading(false);
  }

  function handleChange(e) {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  }

  function handleDateChange(date) {
    if (!date) return;
    setFormData(prevState => ({
      ...prevState,
      availabilitydate: [...prevState.availabilitydate, date]
    }));
  }

  function removeDate(index) {
    setFormData(prevState => ({
      ...prevState,
      availabilitydate: prevState.availabilitydate.filter((_, i) => i !== index)
    }));
  }

  async function handleUpdate(e) {
    e.preventDefault();
    if (!user || !homestay) return;

    let imageUrls = [...formData.images]; // Keep existing images

    if (imageFiles.length > 0) {
      for (const file of imageFiles) {
        const { data, error } = await supabase.storage
          .from("homestay-images")
          .upload(`homestays/${user.id}/${file.name}`, file, { cacheControl: "3600" });

        if (error) {
          console.error("Error uploading image:", error);
        } else {
          console.log("Uploaded file path:", data.path);
          const { data: publicURL } = supabase.storage
            .from("homestay-images")
            .getPublicUrl(data.path);

          console.log("Generated Public URL:", publicURL.publicUrl);
          imageUrls.push(publicURL.publicUrl);
        }
      }
    }

    const { error } = await supabase
      .from("homestay")
      .update({
        name: formData.name,
        description: formData.description,
        facilities: formData.facilities,
        availabilitydate: formData.availabilitydate.map(date => date.toISOString().split("T")[0]),
        location: formData.location,
        googlemapurl: formData.googlemapurl,
        numbedrooms: parseInt(formData.numbedrooms) || 0,
        numbeds: parseInt(formData.numbeds) || 0,
        pricepernight: parseFloat(formData.pricepernight) || 0,
        images: imageUrls,
      })
      .eq("hostid", homestay.hostid);

    if (error) {
      console.error("Error updating homestay:", error);
    } else {
      console.log("Homestay updated successfully!");
      fetchHomestay(user.id);
      setIsEditing(false);
    }
  }

  if (loading) return <div className="flex justify-center items-center h-screen">Loading...</div>;

  return (
    <div className="flex h-screen">
      <Sidebar />
      <main className="flex-1 p-8">
        <h2 className="text-2xl font-semibold">Homestay Profile</h2>

        {homestay && !isEditing ? (
          <div className="bg-white shadow-lg p-6 rounded-lg space-y-4">
            <h3><span className="text-[#8B8B8B]">Homestay Name:</span> {homestay.name}</h3>
            <p><span className="text-[#8B8B8B]">Location:</span> {homestay.location}</p>
            <p><span className="text-[#8B8B8B]">Google Map Location:</span> <a href={homestay.googlemapurl} target="_blank" className="text-blue-500">{homestay.googlemapurl}</a></p>
            <p><span className="text-[#8B8B8B]">Unavailable Dates:</span> {homestay.availabilitydate?.join(", ")}</p>

            {homestay.images.length > 0 && (
              <div className="grid grid-cols-3 gap-4">
                {homestay.images.map((img, index) => (
                  <img key={index} src={img} alt={`Homestay ${index + 1}`} className="w-full h-36 object-cover" />
                ))}
              </div>
            )}

            <button onClick={() => setIsEditing(true)} className="bg-blue-500 text-white px-4 py-2 rounded">Edit</button>
          </div>
        ) : (
          <form onSubmit={handleUpdate} className="mt-4 space-y-4">
            <input type="text" name="name" value={formData.name} onChange={handleChange} className="w-full border p-2 rounded" required />
            <input type="file" multiple accept="image/*" onChange={(e) => setImageFiles([...e.target.files])} className="w-full border p-2 rounded" />

            {formData.images.length > 0 && (
              <div className="grid grid-cols-3 gap-4 mt-2">
                {formData.images.map((img, index) => (
                  <img key={index} src={img} alt={`Existing ${index}`} className="w-full h-36 object-cover" />
                ))}
              </div>
            )}

            <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded">Update</button>
            <button onClick={() => setIsEditing(false)} className="ml-2 bg-gray-500 text-white px-4 py-2 rounded">Cancel</button>
          </form>
        )}
      </main>
    </div>
  );
}

export default Homestay;
