import { useLocation } from "react-router-dom";
import { useEffect, useState } from "react";
import { supabase } from "../utils/supabaseClient";

const Summary = () => {
  const location = useLocation();
  const bookingDetails = location.state || {};
  const [homestay, setHomestay] = useState({ name: "", location: "" });

  useEffect(() => {
    if (bookingDetails.homestayid) {
      fetchHomestayDetails(bookingDetails.homestayid);
    }
  }, [bookingDetails.homestayid]);

  const fetchHomestayDetails = async (homestayid) => {
    const { data, error } = await supabase
      .from("homestay")
      .select("homestayname, location")
      .eq("homestayid", homestayid)
      .single();

    if (data) {
      setHomestay({ name: data.homestayname, location: data.location });
    }
  };

  // Convert string dates to Date objects
  const checkIn = new Date(bookingDetails.checkInDate);
  const checkOut = new Date(bookingDetails.checkOutDate);

  // Calculate number of nights
  const nights = Math.max((checkOut - checkIn) / (1000 * 60 * 60 * 24), 1); // Ensures at least 1 night

  // Calculate total guests
  const totalGuests = (bookingDetails.adults || 0) + (bookingDetails.children || 0);

  // Calculate total cost based on guests and nights
  const totalPrice = bookingDetails.pricePerNight * totalGuests * nights;
  const cleaningFee = 50; // Example fee
  const taxes = totalPrice * 0.1; // 10% tax
  const grandTotal = totalPrice + cleaningFee + taxes;

  return (
    <div className="flex justify-center items-center min-h-screen p-4">
      <div className="bg-white p-6 rounded-2xl shadow-lg max-w-xl w-full">
        <h1 className="text-xl font-bold text-center mb-4">Booking Summary</h1>

        {/* Homestay Details */}
        <div className="mb-4 space-y-1">
          <h2 className="font-semibold">Homestay Details</h2>
          <p className="text-gray-600">Name: {homestay.name || "Loading..."}</p>
          <p className="text-gray-600">Location: {homestay.location || "Loading..."}</p>
        </div>

        {/* Guest Details */}
        <div className="mb-4 space-y-1">
          <h2 className="font-semibold">Guest Details</h2>
          <p className="text-gray-600">Name: {bookingDetails.guestName}</p>
          <p className="text-gray-600">Email: {bookingDetails.email}</p>
        </div>

        {/* Stay Details */}
        <div className="mb-4 space-y-1">
          <h2 className="font-semibold">Stay Details</h2>
          <p className="text-gray-600">Check-in: {bookingDetails.checkInDate}</p>
          <p className="text-gray-600">Check-out: {bookingDetails.checkOutDate}</p>
          <p className="text-gray-600">Nights: {nights}</p>
          <p className="text-gray-600">Adults: {bookingDetails.adults}</p>
          <p className="text-gray-600">Children: {bookingDetails.children}</p>
          <p className="text-gray-600">Total Guests: {totalGuests}</p>
          <p className="text-gray-600">Guest Type: {bookingDetails.guestType}</p>
        </div>

        {/* Payment Details */}
        <div className="mb-4 space-y-1">
          <h2 className="font-semibold">Payment Details</h2>
          <p>Nu.{bookingDetails.pricePerNight} x {totalGuests} guests x {nights} nights: Nu.{totalPrice}</p>
          <p>Cleaning Fee: Nu.{cleaningFee}</p>
          <p>Taxes (10%): Nu.{taxes.toFixed(2)}</p>
          <p className="font-bold">Total: Nu.{grandTotal.toFixed(2)}</p>
        </div>

        <button className="w-full bg-orange-500 text-white py-2 rounded-lg hover:bg-orange-600 mt-4">BOOK NOW</button>
      </div>
    </div>
  );
};

export default Summary;
