import { useParams } from "react-router-dom";

const DistrictPage = () => {
  const { district } = useParams();
  const formattedDistrict = district.replace(/\s+/g, "").toLowerCase(); 
  const imageUrl = `/dzongkhags/${formattedDistrict}.jpg`;

  return (
    <div className="relative w-full h-full">
      {/* Background Image */}
      <img 
        src={imageUrl} 
        alt={district} 
        className="w-full h-[600px] object-cover"
      />

      {/* Overlay and Centered Text */}
      <div className="absolute flex flex-col items-center justify-center text-white bg-black bg-opacity-50">
        <h1 className="text-5xl font-bold">{`WELCOME TO ${district.toUpperCase()}`}</h1>
        <p className="mt-4 text-xl italic">
          Information about {district} will be displayed here.
        </p>
      </div>
    </div>
  );
};

export default DistrictPage;
