import React, { useState, useEffect } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { supabase } from "../utils/supabaseClient";
import { useParams } from "react-router-dom";

const Availability = ({ chatbotVisible }) => {
  const { id } = useParams();
  const today = new Date();
  const tomorrow = new Date();
  tomorrow.setDate(today.getDate() + 1);

  const [adults, setAdults] = useState(1);
  const [children, setChildren] = useState(0);
  const [guestType, setGuestType] = useState("International");
  const [checkInDate, setCheckInDate] = useState(today);
  const [checkOutDate, setCheckOutDate] = useState(tomorrow);
  const [showGuestDetails, setShowGuestDetails] = useState(false);
  const [showSummary, setShowSummary] = useState(false);
  const [unavailableDates, setUnavailableDates] = useState([]);
  const [isAvailable, setIsAvailable] = useState(true);

  // Guest Details State
  const [guestName, setGuestName] = useState("");
  const [guestEmail, setGuestEmail] = useState("");
  const [guestMessage, setGuestMessage] = useState("");

  useEffect(() => {
    async function fetchAvailability() {
      const { data, error } = await supabase
        .from("homestay")
        .select("availabilitydate")
        .eq("homestayid", id)
        .single();

      if (!error && data?.availabilitydate) {
        setUnavailableDates(data.availabilitydate.map(date => new Date(date)));
      }
    }

    fetchAvailability();
  }, [id]);

  useEffect(() => {
    const checkAvailability = () => {
      const selectedDates = [];
      let currentDate = new Date(checkInDate);
      while (currentDate <= checkOutDate) {
        selectedDates.push(new Date(currentDate));
        currentDate.setDate(currentDate.getDate() + 1);
      }

      const unavailable = selectedDates.some(date => 
        unavailableDates.some(unavailableDate => date.toDateString() === unavailableDate.toDateString())
      );
      setIsAvailable(!unavailable);
    };

    checkAvailability();
  }, [checkInDate, checkOutDate, unavailableDates]);

  const handleSubmitGuestDetails = () => {
    setShowGuestDetails(false);
    setShowSummary(true);
  };

  return (
    <div className="flex mt-8 space-x-12 relative">
      {/* Availability Section */}
      <div className={`left ${chatbotVisible ? "w-[55%]" : "w-[60%]"}`}>
        <h1 className="text-[1.2rem] font-semibold">Availability</h1>
        <p className="text-gray-500 mb-4">Select date to check the availability</p>
        <div className="flex space-x-12">
          <div>
            <h1>Check-in</h1>
            <DatePicker
              selected={checkInDate}
              onChange={(date) => setCheckInDate(date)}
              selectsStart
              startDate={checkInDate}
              endDate={checkOutDate}
              minDate={today}
              className="border p-2 rounded-lg"
            />
          </div>
          <div>
            <h1>Check-out</h1>
            <DatePicker
              selected={checkOutDate}
              onChange={(date) => setCheckOutDate(date)}
              selectsEnd
              startDate={checkInDate}
              endDate={checkOutDate}
              minDate={checkInDate}
              className="border p-2 rounded-lg"
            />
          </div>
        </div>
      </div>

      {/* Booking Section */}
      <div className={`right checkout ${chatbotVisible ? "w-[35%]" : "w-[40%]"}`}>
        {!isAvailable ? (
          <div className="text-center text-red-500 text-lg font-semibold mt-6">
            The host is not available on these days
          </div>
        ) : (
          <div className="flex flex-col gap-6 shadow-lg p-6 rounded-lg bg-white">
            <div className="heading flex items-end justify-between">
              <div className="price flex items-end">
                <h1 className="text-2xl font-semibold">Nu.3000</h1>
                <p className="text-[#6b727f]"> / night</p>
              </div>
            </div>

            {/* Book Now Button */}
            <div className="reserve_button">
              <button
                onClick={() => setShowGuestDetails(true)}
                className="w-full tracking-wider py-3 bg-[#FF6600] text-white rounded-lg transition duration-300 ease-in-out transform hover:bg-[#FF6600]/90 active:scale-[95%]"
              >
                BOOK NOW
              </button>
            </div>
          </div>
        )}

        {/* Guest Details Form */}
        {showGuestDetails && (
          <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 mt-12">
            <div className="bg-white p-6 rounded-lg w-[400px] shadow-lg">
              <h2 className="text-xl font-bold">Guest Details</h2>
              <label className="block mb-2">Name:</label>
              <input
                type="text"
                className="w-full border p-2 rounded mb-4"
                value={guestName}
                onChange={(e) => setGuestName(e.target.value)}
              />
              <label className="block mb-2">Email:</label>
              <input
                type="email"
                className="w-full border p-2 rounded mb-4"
                value={guestEmail}
                onChange={(e) => setGuestEmail(e.target.value)}
              />
              <label className="block mb-2">Message:</label>
              <textarea
                className="w-full border p-2 rounded mb-4"
                value={guestMessage}
                onChange={(e) => setGuestMessage(e.target.value)}
              />
              <button onClick={handleSubmitGuestDetails} className="w-full bg-[#FF6600] text-white py-2 rounded">
                Submit
              </button>
            </div>
          </div>
        )}

        {/* Booking Summary */}
        {showSummary && (
          <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 mt-12">
            <div className="bg-white p-6 rounded-lg w-[400px] shadow-lg">
              <h2 className="text-xl font-bold">Booking Summary</h2>
              <p className="mt-2"><strong>Name:</strong> {guestName}</p>
              <p className="mt-1"><strong>Email:</strong> {guestEmail}</p>
              <p className="mt-1"><strong>Check-in:</strong> {checkInDate.toLocaleDateString()}</p>
              <p className="mt-1"><strong>Check-out:</strong> {checkOutDate.toLocaleDateString()}</p>
              <p className="mt-1"><strong>Adults:</strong> {adults}</p>
              <p className="mt-1"><strong>Children:</strong> {children}</p>
              <p className="mt-1"><strong>Guest Type:</strong> {guestType}</p>
              <button onClick={() => setShowSummary(false)} className="w-full bg-[#FF6600] text-white py-2 rounded mt-4">
                Close
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Availability;
