import React, { useState } from "react";
import { ChevronLeft, ChevronRight } from "react-icons/fi";

const districts = [
  "Bumthang", "Chhukha", "Dagana", "Gasa", "Haa",
  "Lhuentse", "Mongar", "Paro", "Pemagatshel",
  "Punakha", "S/Jongkhar"
];

const SubHeader = () => {
  const [selected, setSelected] = useState("Paro");

  const handleScroll = (direction) => {
    const index = districts.indexOf(selected);
    if (direction === "left" && index > 0) {
      setSelected(districts[index - 1]);
    } else if (direction === "right" && index < districts.length - 1) {
      setSelected(districts[index + 1]);
    }
  };

  return (
    <div className="flex items-center justify-center space-x-4 py-2">
      <button onClick={() => handleScroll("left")} className="p-2 rounded-full hover:bg-gray-200">
        <ChevronLeft size={20} />
      </button>
      <div className="flex space-x-4">
        {districts.map((district) => (
          <span
            key={district}
            className={`text-gray-500 cursor-pointer ${
              selected === district ? "font-bold text-black" : ""
            }`}
            onClick={() => setSelected(district)}
          >
            {district}
          </span>
        ))}
      </div>
      <button onClick={() => handleScroll("right")} className="p-2 rounded-full hover:bg-gray-200">
        <ChevronRight size={20} />
      </button>
    </div>
  );
};

export default SubHeader;
