import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../index.css";
import Rightarrow from "./arrowsvgs/Rightarrow";
import Leftarrow from "./arrowsvgs/Leftarrow";
import {
  Travel,
  Trending,
  Restaurant,
  Homestay,
  Hotels,
  Packages,
  FlightBook,
  Filter
} from "./Iconlist";
import Filters from "./Filters";

const Iconbar = ({ chatbotVisible }) => {
  const [isFilterModalOpen, setFilterModalOpen] = useState(false)
  const [activeIndex, setActiveIndex] = useState(-1);
  const icons = [
    { component: Trending, to: "/trending"},
    { component: Homestay, to: "/homestay" },
    { component: Hotels, to: "/hotels" },
    { component: FlightBook, to: "/flightbook" },
    { component: Travel, to: "/travel" },
    { component: Restaurant, to: "/restaurant" },
    { component: Packages, to: "/packages" },
  ];

  const handleRightArrowClick = () => {
    setActiveIndex((prevIndex) => (prevIndex + 1) % icons.length);
    const activeIcon = document.querySelector(".active-icon");
    if (activeIcon) {
      activeIcon.scrollIntoView({
        behavior: "smooth",
        inline: "start",
        block: "nearest",
      });
    }
  };

  const handleLeftArrowClick = () => {
    setActiveIndex(
      (prevIndex) => (prevIndex - 1 + icons.length) % icons.length
    );
    const activeIcon = document.querySelector(".active-icon");
    if (activeIcon) {
      activeIcon.scrollIntoView({
        behavior: "smooth",
        inline: "end",
        block: "nearest",
      });
    }
  };
    const handleFilterButtonClick = () => {
      setFilterModalOpen(true);
    };

    const handleCloseFilterModal = () => {
      setFilterModalOpen(false);
    };

  return (
    <div
      className={`subheader flex items-center justify-between ${
        chatbotVisible ? "w-full" : "w-screen px-[5%]"
      } `}
    >
      <button className="leftarrow" onClick={handleLeftArrowClick}>
        <Leftarrow />
      </button>

      <div
        className={`icons flex flex-nowrap overflow-x-auto gap-20 py-4 ${
          chatbotVisible ? "max-w-[calc(100vw-100px)]" : ""
        }`}
      >
        {icons.map((icon, index) => (
          <Link to={icon.to} key={index}>
            <div
              className={`icon-wrapper ${
                activeIndex === index ? "active-icon" : ""
              }`}
            >
              {React.createElement(icon.component, {
                name: icon.component.name,
                style: { color: activeIndex === index ? "black" : "" },
                className: activeIndex === index ? "active-icon" : "",
              })}
            </div>
          </Link>
        ))}
      </div>
      <div className="flex gap-4 justify-center items-center">
        <button className="rightarrow " onClick={handleRightArrowClick}>
          <Rightarrow />
        </button>
        <button onClick={handleFilterButtonClick}>
          <Filter />
        </button>
      </div>
        <Filters isOpen={isFilterModalOpen} onClose={handleCloseFilterModal} />
    </div>
  );
};

export default Iconbar;
