import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { supabase } from "../../utils/supabaseClient";
import Sidebar from "../../components/Sidebar";

function GuestDetails() {
  const { bookingId } = useParams();
  const navigate = useNavigate();
  const [booking, setBooking] = useState(null);

  useEffect(() => {
    fetchBookingDetails();
  }, []);

  async function fetchBookingDetails() {
    const { data, error } = await supabase
      .from("booking")
      .select("*")
      .eq("bookingid", bookingId)
      .single();

    if (error) {
      console.error("Error fetching booking details:", error);
    } else {
      setBooking(data);
    }
  }

  /** Handle Accepting or Declining a booking */
  async function handleBookingAction(bookingid, action) {
    const { error } = await supabase
      .from("booking")
      .update({ Status: action }) // Add a 'Status' column in BOOKING table
      .eq("bookingid", bookingid);

    if (error) {
      console.error(`Error updating booking status: ${error.message}`);
    } else {
      fetchBookings(); // Refresh bookings after action
    }
  }

  if (!booking) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <main className="flex-1 p-8">
        <button 
          className="bg-gray-200 px-4 py-2 rounded-md mb-6"
          onClick={() => navigate(-1)}
        >
          ‚Üê Back
        </button>
        <h2 className="text-2xl font-bold">Guest Details</h2>

        <div className="mt-6">
          <div className="grid grid-cols-2 gap-4 text-[#535252]">
            <p><strong>Name:</strong> {booking.guestname}</p>
            <p><strong>Email:</strong> {booking.guestemail}</p>
            <p><strong>Check-in:</strong> {booking.checkin}</p>
            <p><strong>Check-out:</strong> {booking.checkout}</p>
            <p><strong>Adults:</strong> {booking.adult}</p>
            <p><strong>Children:</strong> {booking.children}</p>
            <p><strong>Guest Type:</strong> {booking.guesttype}</p>
            <p><strong>Total Price:</strong> {booking.totalprice}</p>
          </div>
          <div className="mt-6">
            <p><strong>Message:</strong></p>
            <p className="text-gray-600 mt-2">{booking.message}</p>
          </div>
        </div>

        {/* Accept & Decline Buttons */}
        <div className="flex space-x-4 mt-6">
        <button 
            className="bg-[#51CF03] text-white px-3 py-1 rounded-full hover:bg-green-600 mr-2"
            onClick={() => handleBookingAction(booking.bookingid, "Accepted")}
        >
            Accept
        </button>
        <button 
            className="bg-[#FF6600] text-white px-3 py-1 rounded-full hover:bg-red-600"
            onClick={() => handleBookingAction(booking.BookingId, "Declined")}
        >
            Decline
        </button>
        </div>
      </main>
    </div>
  );
}

export default GuestDetails;
