import React, { useState } from "react";
import { Link, useParams } from "react-router-dom";
import homestaysData from "../data/homestaysData";

const HomestayList = () => {
  const { district } = useParams();
  const formattedDistrict = district.replace(/\s+/g, "").toLowerCase();
  const homestays = homestaysData[formattedDistrict] || [];

  const [searchTerm, setSearchTerm] = useState("");
  const [sortOrder, setSortOrder] = useState("asc"); // Sort by price

  // Filter by search term
  const filteredHomestays = homestays.filter(homestay =>
    homestay.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Sort homestays by price
  const sortedHomestays = filteredHomestays.sort((a, b) =>
    sortOrder === "asc" ? a.price - b.price : b.price - a.price
  );

  return (
    <div className="max-w-6xl mx-auto p-6">
      <h1 className="text-3xl font-semibold italic text-center py-4">
        Discover Authentic Homestays in {district.charAt(0).toUpperCase() + district.slice(1)}
      </h1>

      <p className="text-center italic text-gray-600 mb-6">
        Stay Local. Feel at Home. Explore Bhutan Like a Local.
      </p>

      {/* Search & Sort Controls */}
      <div className="flex justify-between items-center mb-6">
        <input
          type="text"
          placeholder="Search by name"
          className="border px-4 py-2 rounded-md w-1/3"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button
          onClick={() => setSortOrder(sortOrder === "asc" ? "desc" : "asc")}
          className="border px-4 py-2 rounded-md"
        >
          {sortOrder === "asc" ? "⬆️ Price: Low to High" : "⬇️ Price: High to Low"}
        </button>
      </div>

      {/* Homestay Listings */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {sortedHomestays.length > 0 ? (
          sortedHomestays.map((homestay) => (
            <Link
              key={homestay.id}
              to={`/homestay/${homestay.id}`}
              className="border rounded-lg overflow-hidden shadow-lg"
            >
              <img
                src={homestay.image}
                alt={homestay.name}
                className="w-full h-48 object-cover"
              />
              <div className="p-4">
                <h2 className="text-xl font-semibold">{homestay.name}</h2>
                <p className="text-gray-500">{homestay.location}</p>
                <p className="text-gray-600">{homestay.date}</p>
                <div className="flex justify-between items-center mt-2">
                  <span className="font-bold">Nu.{homestay.price} / night</span>
                  <span className="text-yellow-500 font-semibold">⭐ {homestay.rating}</span>
                </div>
              </div>
            </Link>
          ))
        ) : (
          <p className="text-center text-gray-500">No homestays available in {district} yet.</p>
        )}
      </div>
    </div>
  );
};

export default HomestayList;
