import { useUser } from "@clerk/clerk-react";
import { supabase } from "../../utils/supabaseClient";
import Sidebar from "../../components/Sidebar";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const dzongkhags = [
  "Bumthang", "Chhukha", "Dagana", "Gasa", "Haa", "Lhuentse", "Mongar", 
  "Paro", "Pema Gatshel", "Punakha", "Samdrup Jongkhar", "Samtse", "Sarpang",
  "Thimphu", "Trashi Yangtse", "Trashigang", "Trongsa", "Tsirang", "Wangdue Phodrang", "Zhemgang"
];

function Homestay() {
  const { user } = useUser();
  const [homestay, setHomestay] = useState(null);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    facilities: "",
    availabilitydate: [],
    location: "",
    googlemapurl: "",
    images: "",
    pricepernight: "",
  });

  useEffect(() => {
    if (user) {
      fetchHomestay();
    }
  }, [user]); // ✅ Depend only on `user` to prevent infinite loops

  /** Fetch the homestay for the logged-in host */
  async function fetchHomestay() {
    setLoading(true);
    try {
      // Fetch the correct host ID from Supabase
      const { data: hostData, error: hostError } = await supabase
        .from("host") // Ensure this is the correct table
        .select("hostid")
        .eq("clerkuserid", user.id) // Match Clerk user ID with Supabase field
        .single();

      if (hostError || !hostData) {
        console.error("Error fetching host ID:", hostError);
        setLoading(false);
        return;
      }

      const hostId = hostData.hostid; // ✅ This is the correct UUID

      // Now fetch the homestay with the correct hostId
      const { data, error } = await supabase
        .from("homestay")
        .select("*")
        .eq("hostid", hostId) // Use the correct UUID
        .single();

      if (error) {
        console.error("Error fetching homestay:", error);
      } else {
        console.log("Fetched homestay:", data);
        setHomestay(data);
      }
    } catch (err) {
      console.error("Unexpected error:", err);
    }
    setLoading(false);
  }




  /** Handle form input changes */
  function handleChange(e) {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  }

  /** Handle availability date selection */
  function handleDateChange(date) {
    if (!date) return; // Avoid null errors
    setFormData(prevState => ({
      ...prevState,
      availabilitydate: [...prevState.availabilitydate, date] // Append date to array
    }));
  }

  function removeDate(index) {
    setFormData(prevState => ({
      ...prevState,
      availabilitydate: prevState.availabilitydate.filter((_, i) => i !== index)
    }));
  }

  /** Handle homestay submission */
  async function handleSubmit(e) {
    e.preventDefault();
    if (!user) return;

    // Fetch the correct host ID from Supabase
    const { data: hostData, error: hostError } = await supabase
      .from("host")
      .select("hostid")
      .eq("clerkuserid", user.id)
      .single();

    if (hostError || !hostData) {
      console.error("Error fetching host ID:", hostError);
      return;
    }

    const hostId = hostData.hostid; // ✅ Use UUID from Supabase, not Clerk user ID

    // Insert homestay using correct hostid
    const { error } = await supabase.from("homestay").insert([
      {
        hostid: hostId, // ✅ Ensure this is a UUID
        name: formData.name,
        description: formData.description,
        facilities: formData.facilities,
        availabilitydate: formData.availabilitydate.map(date => date.toISOString().split("T")[0]), // Convert to YYYY-MM-DD
        location: formData.location,
        googlemapurl: formData.googlemapurl,
        images: formData.images.split(","), 
        pricepernight: formData.pricepernight,
      },
    ]);

    if (error) {
      console.error("Error adding homestay:", error);
    } else {
      console.log("Homestay added successfully!");
      fetchHomestay(); // ✅ Force UI update after insertion
    }
  }



  if (loading) return <div className="flex justify-center items-center h-screen">Loading...</div>;

  return (
    <div className="flex h-screen">
      <Sidebar />
      <main className="flex-1 p-8">
        <h2 className="text-2xl font-semibold">Homestay Management</h2>

        {homestay ? (
          <div className="mt-6">
            <h3 className="text-xl font-semibold">{homestay.name}</h3>
            <p className="text-gray-600 mt-2">{homestay.description}</p>
            <p className="text-gray-600 mt-2"><strong>Facilities:</strong> {homestay.facilities}</p>
            <p className="text-gray-600 mt-2"><strong>Availability Dates:</strong> {homestay.availabilitydate.join(", ")}</p>
            <p className="text-gray-600 mt-2"><strong>Location:</strong> {homestay.location}</p>
            <p className="text-gray-600 mt-2"><strong>Google Map:</strong> <a href={homestay.googlemapurl} target="_blank" rel="noopener noreferrer">View on Google Maps</a></p>
            <p className="text-gray-600 mt-2"><strong>Price per Night:</strong> Nu. {homestay.pricepernight}</p>
            <div className="mt-4">
              <h4 className="text-lg font-semibold">Images</h4>
              <div className="flex gap-2 mt-2">
                {homestay.images.map((img, index) => (
                  <img key={index} src={img} alt="Homestay" className="w-32 h-32 object-cover rounded-lg" />
                ))}
              </div>
            </div>
            <button className="bg-blue-500 text-white px-4 py-2 rounded mt-4">Edit</button>
          </div>
        ) : (
          <div className="mt-6">
            <p className="text-gray-600">You have not listed any homestay yet. Add your homestay below:</p>
            <form onSubmit={handleSubmit} className="mt-4 space-y-4">
              <input type="text" name="name" placeholder="Homestay Name" className="w-full border p-2 rounded" value={formData.name} onChange={handleChange} required />
              <textarea name="description" placeholder="Description" className="w-full border p-2 rounded" value={formData.description} onChange={handleChange} required />
              <input type="text" name="facilities" placeholder="Facilities (comma-separated)" className="w-full border p-2 rounded" value={formData.facilities} onChange={handleChange} required />

            {/* Multi-Date Availability Selection */}
            <div>
              <label className="block text-gray-700 font-semibold">Availability Dates:</label>
              <DatePicker
                selected={null}
                onChange={(date) => {
                  if (date) {
                    setFormData(prevState => ({
                      ...prevState,
                      availabilitydate: [...prevState.availabilitydate, date]
                    }));
                  }
                }}
                dateFormat="yyyy-MM-dd"
                className="w-full border p-2 rounded"
                placeholderText="Select available dates"
              />

              {/* Display Selected Dates */}
              <div className="mt-2 flex flex-wrap gap-2">
                {formData.availabilitydate.map((date, index) => (
                  <div key={index} className="flex items-center bg-gray-200 px-2 py-1 rounded">
                    <span>{date.toISOString().split("T")[0]}</span>
                    <button
                      type="button"
                      className="ml-2 text-red-500"
                      onClick={() => removeDate(index)}
                    >
                      ❌
                    </button>
                  </div>
                ))}
              </div>
            </div>


              {/* Location Dropdown */}
              <select
                name="location"
                value={formData.location}
                onChange={handleChange}
                className="w-full border p-2 rounded"
                required
              >
                <option value="" disabled>Select Dzongkhag</option>
                {dzongkhags.map((dzongkhag, index) => (
                  <option key={index} value={dzongkhag}>{dzongkhag}</option>
                ))}
              </select>

              <input type="text" name="googlemapurl" placeholder="Google Map URL" className="w-full border p-2 rounded" value={formData.googlemapurl} onChange={handleChange} required />
              <input type="text" name="images" placeholder="Image URLs (comma-separated)" className="w-full border p-2 rounded" value={formData.images} onChange={handleChange} required />
              <input type="number" name="pricepernight" placeholder="Price per Night (Nu.)" className="w-full border p-2 rounded" value={formData.pricepernight} onChange={handleChange} required />

              <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded">Add Homestay</button>
            </form>
          </div>
        )}
      </main>
    </div>
  );
}

export default Homestay;


