import { useParams } from "react-router-dom";
import BumthangImage from "../assets/dzongkhags/bumthang.jpg";


const DistrictPage = () => {
  const { district } = useParams();
  const formattedDistrict = district.replace(/\s+/g, "").toLowerCase(); 
  const imageUrl = `../assets/dzongkhags/${formattedDistrict}.jpg`; 

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-4">Welcome to {district}</h1>
      <img 
        src={BumthangImage} 
        alt={`${district}`} 
        className="w-full h-64 object-cover rounded-lg shadow-lg"
      />
      <p className="mt-4 text-lg">Information about {district} will be displayed here.</p>
    </div>
  );
};

export default DistrictPage;
